const buildTreeData = (users: User[]): TreeNode => {
    const buildNode = (user: User): TreeNode => {
        const children = users.filter(u => u.managerId === user.id).map(buildNode);
        return {
            name: user.name,
            children,
            data: user // Store the entire user object in data
        };
    };

    const rootUser = users.find(user => !user.managerId);
    return rootUser ? buildNode(rootUser) : { name: 'No Data', children: [], data: null };
};

