import React, { useState, useEffect } from 'react';
import { 
    Grid, 
    Card, 
    CardContent, 
    CardMedia, 
    Typography, 
    TextField, 
    Box, 
    Select, 
    MenuItem, 
    FormControl, 
    InputLabel, 
    Pagination,
    Drawer,
    IconButton,
    Button,
    useTheme,
    useMediaQuery,
    Stack,
    OutlinedInput
} from '@mui/material';
import FilterListIcon from '@mui/icons-material/FilterList';
import CloseIcon from '@mui/icons-material/Close';
import { User } from '../types/User';
import axios from 'axios';

const PEXELS_API_KEY = 'mIFzGvoWn6QuExlYm7tvsJDagoQLvzv9DJwiM5B2pzyaaCvPw46AFsQk';

// Function to determine likely gender based on first name
const getGenderFromName = (name: string): 'male' | 'female' => {
    const firstName = name.split(' ')[0].toLowerCase();
    // Common female name endings and specific names
    const femaleIndicators = ['a', 'na', 'ia', 'lia', 'emma', 'sarah', 'jane', 'maria', 'sophie', 'jennifer', 'anna', 'julia', 'nina', 'isabella', 'lisa'];

    // Check if the name matches any female indicators
    if (femaleIndicators.some(indicator => 
        firstName === indicator || firstName.endsWith(indicator)
    )) {
        return 'female';
    }

    return 'male'; // Default to male if no female indicators are found
};

// Mock data - replace with actual API call in production
const mockUsers: User[] = [
  // Engineering Team
  { id: 1, name: 'John Doe', team: 'Engineering', location: 'New York', jobRole: 'Software Engineer', imageUrl: '' },
