import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Navigation from './components/Navigation';
import UserList from './components/UserList';
import Dashboard from './components/Dashboard';
import OrgChart from './components/OrgChart';

// Accessible monotone blue theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#0d47a1', // Darker blue for better contrast - WCAG AAA compliant
      light: '#5472d3',
      dark: '#002171',
      contrastText: '#ffffff',
    },
    secondary: {
      main: '#1976d2', // Moved previous primary to secondary
      light: '#4791db',
      dark: '#115293',
      contrastText: '#ffffff',
    },
    background: {
      default: '#f5f8fa', // Light blue-tinted background
      paper: '#ffffff',
    },
    text: {
      primary: '#1a237e', // Dark blue text - WCAG AAA compliant
      secondary: '#455a64', // Blue-grey text for secondary information
    },
    error: {
      main: '#d32f2f',
      contrastText: '#ffffff',
    },
    warning: {
      main: '#ed6c02',
      contrastText: '#ffffff',
    },
    success: {
      main: '#2e7d32',
      contrastText: '#ffffff',
    },
    divider: 'rgba(13, 71, 161, 0.12)', // Updated divider color to match new primary
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      color: '#1a237e',
      fontWeight: 500,
    },
    h2: {
      color: '#1a237e',
      fontWeight: 500,
    },
    h3: {
      color: '#1a237e',
      fontWeight: 500,
    },
    h4: {
      color: '#1a237e',
      fontWeight: 500,
    },
    h5: {
      color: '#1a237e',
      fontWeight: 500,
    },
    h6: {
      color: '#1a237e',
      fontWeight: 500,
    },
    subtitle1: {
      color: '#455a64',
    },
    subtitle2: {
      color: '#455a64',
    },
  },
  components: {
    MuiAppBar: {
      styleOverrides: {
        root: {
          backgroundColor: '#0d47a1', // Ensuring AppBar uses the darker blue
          color: '#ffffff', // Ensuring text is white for maximum contrast
          '& .MuiIconButton-root': {
            color: '#ffffff', // Ensuring icons are white
          },
          '& .MuiTypography-root': {
            color: '#ffffff', // Ensuring all typography in AppBar is white
          },
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          textTransform: 'none',
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          boxShadow: '0 2px 8px rgba(13, 71, 161, 0.1)',
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 12,
        },
      },
    },
    MuiListItemIcon: {
      styleOverrides: {
        root: {
          color: '#0d47a1', // Ensuring list icons use primary color
        },
      },
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Box sx={{ display: 'flex' }}>
          <Navigation />
          <Box component="main" sx={{ flexGrow: 1, height: '100vh', overflow: 'auto', mt: 8, p: 3 }}>
            <Routes>
              <Route path="/" element={<Dashboard />} />
              <Route path="/users" element={<UserList />} />
              <Route path="/org-chart" element={<OrgChart />} />
            </Routes>
          </Box>
        </Box>
      </Router>
    </ThemeProvider>
  );
}

export default App;
