import React, { useState } from 'react';
import { Box, Grid, Paper, Typography, FormControl, InputLabel, Select, MenuItem, OutlinedInput, Chip, Theme, useTheme, Button } from '@mui/material';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import { User } from '../types/User';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

// Mock data - using the same data as UserList component
const mockUsers: User[] = [
  { id: 1, name: 'John Doe', team: 'Engineering', location: 'New York', jobRole: 'Software Engineer', imageUrl: '' },
  { id: 2, name: 'Jane Smith', team: 'Design', location: 'San Francisco', jobRole: 'UI Designer', imageUrl: '' },
  { id: 3, name: 'Mike Johnson', team: 'Marketing', location: 'London', jobRole: 'Marketing Manager', imageUrl: '' },
  { id: 4, name: 'Sarah Williams', team: 'Engineering', location: 'Berlin', jobRole: 'Frontend Developer', imageUrl: '' },
  { id: 5, name: 'Tom Brown', team: 'Product', location: 'Toronto', jobRole: 'Product Manager', imageUrl: '' },
  { id: 6, name: 'Emily Davis', team: 'Engineering', location: 'Sydney', jobRole: 'Backend Developer', imageUrl: '' },
  { id: 7, name: 'David Wilson', team: 'Design', location: 'Paris', jobRole: 'UX Designer', imageUrl: '' },
  { id: 8, name: 'Lisa Anderson', team: 'Marketing', location: 'Tokyo', jobRole: 'Content Strategist', imageUrl: '' },
  { id: 9, name: 'James Taylor', team: 'Product', location: 'Singapore', jobRole: 'Product Owner', imageUrl: '' },
  { id: 10, name: 'Maria Garcia', team: 'Engineering', location: 'Madrid', jobRole: 'DevOps Engineer', imageUrl: '' },
  { id: 11, name: 'Robert Martinez', team: 'Design', location: 'Barcelona', jobRole: 'Visual Designer', imageUrl: '' },
  { id: 12, name: 'Jennifer Lee', team: 'Marketing', location: 'Seoul', jobRole: 'Marketing Specialist', imageUrl: '' }
];

const Dashboard: React.FC = () => {
  // Process data for Roles by Location
  const rolesByLocation = mockUsers.reduce((acc: Record<string, Record<string, number>>, user: User) => {
    if (!acc[user.jobRole]) {
      acc[user.jobRole] = {};
    }
    acc[user.jobRole][user.location] = (acc[user.jobRole][user.location] || 0) + 1;
    return acc;
  }, {});

  // Process data for Roles by Team
  const rolesByTeam = mockUsers.reduce((acc: Record<string, Record<string, number>>, user: User) => {
    if (!acc[user.jobRole]) {
      acc[user.jobRole] = {};
    }
    acc[user.jobRole][user.team] = (acc[user.jobRole][user.team] || 0) + 1;
    return acc;
  }, {});

  // Process data for Teams by Location
  const teamsByLocation = mockUsers.reduce((acc: Record<string, Record<string, number>>, user: User) => {
    if (!acc[user.team]) {
      acc[user.team] = {};
    }
    acc[user.team][user.location] = (acc[user.team][user.location] || 0) + 1;
    return acc;
  }, {});

  // Add state for legend visibility
  const [roleLocationVisibility, setRoleLocationVisibility] = useState<Record<string, boolean>>({});
  const [roleTeamVisibility, setRoleTeamVisibility] = useState<Record<string, boolean>>({});
  const [teamLocationVisibility, setTeamLocationVisibility] = useState<Record<string, boolean>>({});

  // Initialize visibility state when data changes
  React.useEffect(() => {
    const initVisibility = (data: any) => 
      Object.keys(data).reduce((acc, key) => ({ ...acc, [key]: true }), {});

    setRoleLocationVisibility(initVisibility(rolesByLocation));
    setRoleTeamVisibility(initVisibility(rolesByTeam));
    setTeamLocationVisibility(initVisibility(teamsByLocation));
  }, []);

  // Generate accessible colors with proper contrast
  const generateAccessibleColors = (count: number): { background: string; text: string }[] => {
    return Array.from({ length: count }, (_, i) => {
      const hue = (i * 360) / count;
      // Using darker, more saturated colors for better contrast
      const background = `hsl(${hue}, 65%, 35%)`;
      // Using white or black text based on background color's brightness
      const textColor = hue > 50 && hue < 190 ? '#000000' : '#ffffff';
      return { background, text: textColor };
    });
  };

  // Custom legend components
  const LegendSelect = ({ items, visibility, setVisibility, title }: {
    items: string[];
    visibility: Record<string, boolean>;
    setVisibility: (value: Record<string, boolean>) => void;
    title: string;
  }) => {
    const theme = useTheme();
    const colors = generateAccessibleColors(items.length);

    const ITEM_HEIGHT = 48;
    const ITEM_PADDING_TOP = 8;
    const MenuProps = {
      PaperProps: {
        style: {
          maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
          width: 250,
        },
      },
    };

    function getStyles(item: string, selectedItems: readonly string[], theme: Theme) {
      return {
        fontWeight:
          selectedItems.indexOf(item) === -1
            ? theme.typography.fontWeightRegular
            : theme.typography.fontWeightMedium,
      };
    }

    const handleChange = (event: any) => {
      const selectedItems = event.target.value as string[];
      const newVisibility = items.reduce((acc, item) => ({
        ...acc,
        [item]: selectedItems.includes(item)
      }), {});
      setVisibility(newVisibility);
    };

    const handleDelete = (itemToDelete: string) => {
      const newSelectedItems = selectedItems.filter(item => item !== itemToDelete);
      const newVisibility = items.reduce((acc, item) => ({
        ...acc,
        [item]: newSelectedItems.includes(item)
      }), {});
      setVisibility(newVisibility);
    };

    const handleClearAll = () => {
      const newVisibility = items.reduce((acc, item) => ({
        ...acc,
        [item]: false
      }), {});
      setVisibility(newVisibility);
    };

    const handleKeyDown = (event: React.KeyboardEvent, value: string) => {
      if (event.key === 'Backspace' || event.key === 'Delete') {
        handleDelete(value);
      }
    };

    const selectedItems = Object.entries(visibility)
      .filter(([_, isVisible]) => isVisible)
      .map(([item]) => item);

    return (
      <Box sx={{ mt: 2 }}>
        <Box sx={{ display: 'flex', alignItems: 'flex-start', gap: 1 }}>
          <FormControl fullWidth size="small">
            <InputLabel id={`${title}-label`}>{title}</InputLabel>
            <Select
              labelId={`${title}-label`}
              multiple
              value={selectedItems}
              onChange={handleChange}
              input={<OutlinedInput label={title} />}
              renderValue={(selected) => (
                <Box sx={{ py: 0.5 }}>
                  {(selected as string[]).length} selected
                </Box>
              )}
              MenuProps={{
                ...MenuProps,
                PaperProps: {
                  ...MenuProps.PaperProps,
                  'aria-label': `${title} selection menu`,
                }
              }}
            >
              {items.map((item, index) => {
                const { background, text } = colors[index];
                return (
                  <MenuItem
                    key={item}
                    value={item}
                    style={getStyles(item, selectedItems, theme)}
                    role="option"
                    aria-selected={selectedItems.includes(item)}
                  >
                    <Box
                      component="span"
                      sx={{
                        width: 14,
                        height: 14,
                        mr: 1,
                        backgroundColor: background,
                        display: 'inline-block',
                        borderRadius: '2px',
                      }}
                      role="presentation"
                    />
                    {item}
                  </MenuItem>
                );
              })}
            </Select>
          </FormControl>
          {selectedItems.length > 0 && (
            <Button
              size="small"
              onClick={handleClearAll}
              sx={{
                minWidth: 'auto',
                height: 40,
                whiteSpace: 'nowrap',
                color: 'text.secondary',
                '&:hover': {
                  backgroundColor: 'action.hover',
                }
              }}
              aria-label="Clear all selections"
            >
              Clear All
            </Button>
          )}
        </Box>

        {selectedItems.length > 0 && (
          <Box 
            sx={{ 
              display: 'flex', 
              flexWrap: 'wrap', 
              gap: 0.5, 
              mt: 1,
              p: 1,
              border: 1,
              borderColor: 'divider',
              borderRadius: 1,
              backgroundColor: 'background.paper'
            }}
            role="list"
          >
            {selectedItems.map((value) => {
              const colorIndex = items.indexOf(value);
              const { background, text } = colors[colorIndex];
              return (
                <Chip
                  key={value}
                  label={value}
                  size="small"
                  variant="outlined"
                  onDelete={() => handleDelete(value)}
                  onKeyDown={(e) => handleKeyDown(e, value)}
                  sx={{
                    borderColor: background,
                    color: background,
                    backgroundColor: 'transparent',
                    '& .MuiChip-label': {
                      fontSize: '0.875rem',
                      fontWeight: 500,
                      padding: '4px 8px',
                    },
                    '& .MuiChip-deleteIcon': {
                      color: background,
                      fontSize: '1.25rem',
                      '&:hover': {
                        color: background,
                        opacity: 0.7,
                      },
                    },
                    '&:hover': {
                      backgroundColor: `${background}10`,
                    },
                    '&:focus': {
                      outline: `2px solid ${theme.palette.primary.main}`,
                      outlineOffset: '2px',
                    },
                    '&:focus-visible': {
                      outline: `2px solid ${theme.palette.primary.main}`,
                      outlineOffset: '2px',
                    },
                  }}
                  role="listitem"
                  aria-label={`Remove ${value}`}
                  deleteIcon={
                    <span aria-hidden="true" style={{ display: 'flex', alignItems: 'center' }}>
                      ×
                    </span>
                  }
                />
              );
            })}
          </Box>
        )}
      </Box>
    );
  };

  // Update chart options with custom legend
  const getChartOptions = (title: string) => ({
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false, // Hide default legend
      },
      title: {
        display: true,
        text: title,
        padding: {
          bottom: 10
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 1
        }
      }
    }
  });

  // Filter datasets based on visibility
  const rolesByLocationData = {
    labels: Array.from(new Set(mockUsers.map(user => user.location))),
    datasets: Object.entries(rolesByLocation)
      .filter(([role]) => roleLocationVisibility[role] !== false)
      .map(([role, locations], index) => ({
        label: role,
        data: Object.values(locations),
        backgroundColor: generateAccessibleColors(Object.keys(rolesByLocation).length)[index].background,
      }))
  };

  const rolesByTeamData = {
    labels: Array.from(new Set(mockUsers.map(user => user.team))),
    datasets: Object.entries(rolesByTeam)
      .filter(([role]) => roleTeamVisibility[role] !== false)
      .map(([role, teams], index) => ({
        label: role,
        data: Object.values(teams),
        backgroundColor: generateAccessibleColors(Object.keys(rolesByTeam).length)[index].background,
      }))
  };

  const teamsByLocationData = {
    labels: Array.from(new Set(mockUsers.map(user => user.location))),
    datasets: Object.entries(teamsByLocation)
      .filter(([team]) => teamLocationVisibility[team] !== false)
      .map(([team, locations], index) => ({
        label: team,
        data: Object.values(locations),
        backgroundColor: generateAccessibleColors(Object.keys(teamsByLocation).length)[index].background,
      }))
  };

  return (
    <Box sx={{ flexGrow: 1, p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Analytics Dashboard
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ height: 320 }}>
              <Bar data={rolesByLocationData} options={getChartOptions('Roles by Location')} />
            </Box>
            <LegendSelect
              items={Object.keys(rolesByLocation)}
              visibility={roleLocationVisibility}
              setVisibility={setRoleLocationVisibility}
              title="Select Roles"
            />
          </Paper>
        </Grid>

        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ height: 320 }}>
              <Bar data={rolesByTeamData} options={getChartOptions('Roles by Team')} />
            </Box>
            <LegendSelect
              items={Object.keys(rolesByTeam)}
              visibility={roleTeamVisibility}
              setVisibility={setRoleTeamVisibility}
              title="Select Roles"
            />
          </Paper>
        </Grid>

        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ height: 320 }}>
              <Bar data={teamsByLocationData} options={getChartOptions('Teams by Location')} />
            </Box>
            <LegendSelect
              items={Object.keys(teamsByLocation)}
              visibility={teamLocationVisibility}
              setVisibility={setTeamLocationVisibility}
              title="Select Teams"
            />
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
