import React from 'react';
import { OrganizationGraph } from '@ant-design/graphs';
import {
    Box,
    Typography,
    Card,
    CardContent,
    useTheme,
    Avatar,
    Link,
    Stack,
    Tooltip
} from '@mui/material';
import {
    Email as EmailIcon,
    Phone as PhoneIcon,
    LinkedIn as LinkedInIcon,
    LocationOn as LocationIcon,
    Business as BusinessIcon
} from '@mui/icons-material';
import { User } from '../types/User';

// Mock data with hierarchy
const mockUsers: User[] = [
    {
        id: 1,
        name: 'John Doe',
        team: 'Executive',
        location: 'New York',
        jobRole: 'CEO',
        imageUrl: '',
        email: 'john.doe@company.com',
        phone: '+1 (555) 000-0001',
        linkedin: 'linkedin.com/in/johndoe',
        directReports: [2, 3]
    },
    {
        id: 2,
        name: 'Sarah Williams',
        team: 'Engineering',
        location: 'San Francisco',
        jobRole: 'CTO',
        imageUrl: '',
        email: 'sarah.williams@company.com',
        phone: '+1 (555) 000-0002',
        linkedin: 'linkedin.com/in/sarahwilliams',
        managerId: 1,
        directReports: [4, 5]
    },
    {
        id: 3,
        name: 'Michael Brown',
        team: 'Operations',
        location: 'Chicago',
        jobRole: 'COO',
        imageUrl: '',
        email: 'michael.brown@company.com',
        phone: '+1 (555) 000-0003',
        linkedin: 'linkedin.com/in/michaelbrown',
        managerId: 1,
        directReports: [6, 7]
    },
    {
        id: 4,
        name: 'Emily Davis',
        team: 'Engineering',
        location: 'Seattle',
        jobRole: 'Engineering Manager',
        imageUrl: '',
        email: 'emily.davis@company.com',
        phone: '+1 (555) 000-0004',
        linkedin: 'linkedin.com/in/emilydavis',
        managerId: 2
    },
    {
        id: 5,
        name: 'David Wilson',
        team: 'Engineering',
        location: 'Boston',
        jobRole: 'Tech Lead',
        imageUrl: '',
        email: 'david.wilson@company.com',
        phone: '+1 (555) 000-0005',
        linkedin: 'linkedin.com/in/davidwilson',
        managerId: 2
    },
    {
        id: 6,
        name: 'Lisa Anderson',
        team: 'Operations',
        location: 'Miami',
        jobRole: 'Operations Manager',
        imageUrl: '',
        email: 'lisa.anderson@company.com',
        phone: '+1 (555) 000-0006',
        linkedin: 'linkedin.com/in/lisaanderson',
        managerId: 3
    },
    {
        id: 7,
        name: 'Robert Taylor',
        team: 'Operations',
        location: 'Houston',
        jobRole: 'Project Manager',
        imageUrl: '',
        email: 'robert.taylor@company.com',
        phone: '+1 (555) 000-0007',
        linkedin: 'linkedin.com/in/roberttaylor',
        managerId: 3
    }
];

const OrgChart: React.FC = () => {
    const theme = useTheme();

    const renderNode = (node: any) => {
        const { value } = node;
        return (
            <foreignObject width={280} height={280} x={-140} y={-140}>
                <Card sx={{ 
                    width: '100%',
                    height: '100%',
                    '&:hover': {
                        boxShadow: theme.shadows[8],
                        transform: 'translateY(-2px)',
                        transition: 'all 0.2s'
                    }
                }}>
                    <CardContent>
                        <Stack spacing={2}>
                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                                <Avatar
                                    src={value.imageUrl || `https://ui-avatars.com/api/?name=${encodeURIComponent(value.name)}&background=random`}
                                    alt={value.name}
                                    sx={{ width: 56, height: 56 }}
                                />
                                <Box>
                                    <Typography variant="h6" sx={{ fontSize: '1rem' }}>
                                        {value.name}
                                    </Typography>
                                    <Typography color="text.secondary" variant="body2">
                                        {value.title}
                                    </Typography>
                                </Box>
                            </Box>

                            <Stack spacing={1}>
                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                    <BusinessIcon fontSize="small" color="action" />
                                    <Typography variant="body2" color="text.secondary">
                                        {value.team}
                                    </Typography>
                                </Box>
                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                    <LocationIcon fontSize="small" color="action" />
                                    <Typography variant="body2" color="text.secondary">
                                        {value.location}
                                    </Typography>
                                </Box>
                                <Link 
                                    href={`mailto:${value.email}`}
                                    sx={{ 
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: 1,
                                        color: 'inherit',
                                        textDecoration: 'none',
                                        '&:hover': { color: theme.palette.primary.main }
                                    }}
                                >
                                    <EmailIcon fontSize="small" color="action" />
                                    <Typography variant="body2" noWrap>
                                        {value.email}
                                    </Typography>
                                </Link>
                                <Link 
                                    href={`tel:${value.phone}`}
                                    sx={{ 
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: 1,
                                        color: 'inherit',
                                        textDecoration: 'none',
                                        '&:hover': { color: theme.palette.primary.main }
                                    }}
                                >
                                    <PhoneIcon fontSize="small" color="action" />
                                    <Typography variant="body2">
                                        {value.phone}
                                    </Typography>
                                </Link>
                                <Link 
                                    href={`https://www.${value.linkedin}`}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    sx={{ 
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: 1,
                                        color: 'inherit',
                                        textDecoration: 'none',
                                        '&:hover': { color: theme.palette.primary.main }
                                    }}
                                >
                                    <LinkedInIcon fontSize="small" color="action" />
                                    <Typography variant="body2" noWrap>
                                        {value.linkedin}
                                    </Typography>
                                </Link>
                            </Stack>
                        </Stack>
                    </CardContent>
                </Card>
            </foreignObject>
        );
    };

    const config = {
        data: {
            id: 'root',
            value: {
                name: mockUsers[0].name,
                title: mockUsers[0].jobRole,
                team: mockUsers[0].team,
                location: mockUsers[0].location,
                email: mockUsers[0].email,
                phone: mockUsers[0].phone,
                linkedin: mockUsers[0].linkedin,
                imageUrl: mockUsers[0].imageUrl
            },
            children: mockUsers
                .filter(user => user.managerId === mockUsers[0].id)
                .map(user => ({
                    id: user.id.toString(),
                    value: {
                        name: user.name,
                        title: user.jobRole,
                        team: user.team,
                        location: user.location,
                        email: user.email,
                        phone: user.phone,
                        linkedin: user.linkedin,
                        imageUrl: user.imageUrl
                    },
                    children: mockUsers
                        .filter(child => child.managerId === user.id)
                        .map(child => ({
                            id: child.id.toString(),
                            value: {
                                name: child.name,
                                title: child.jobRole,
                                team: child.team,
                                location: child.location,
                                email: child.email,
                                phone: child.phone,
                                linkedin: child.linkedin,
                                imageUrl: child.imageUrl
                            }
                        }))
                }))
        },
        nodeCfg: {
            size: [280, 280],
            style: {
                fill: 'transparent',
                stroke: 'transparent',
            },
            nodeStateStyles: {
                hover: {
                    stroke: theme.palette.primary.main,
                },
            },
            label: {
                style: {
                    fill: 'transparent',
                },
            },
        },
        edgeCfg: {
            style: {
                stroke: theme.palette.primary.main,
                lineWidth: 1,
            },
        },
        layout: {
            type: 'dendrogram',
            direction: 'TB',
            nodeSep: 50,
            rankSep: 300,
        },
        behaviors: ['drag-canvas', 'zoom-canvas', 'drag-node'],
        autoFit: true,
        renderer: 'svg',
        customContent: renderNode,
    };

    return (
        <Box sx={{ width: '100%', pt: 2 }}>
            <Typography variant="h4" component="h1" sx={{ mb: 4 }}>
                Organization Chart
            </Typography>
            <Card sx={{ height: 'calc(100vh - 180px)' }}>
                <CardContent sx={{ height: '100%' }}>
                    <OrganizationGraph {...config} />
                </CardContent>
            </Card>
        </Box>
    );
};

export default OrgChart;
