import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Switch,
  FormControl,
  FormControlLabel,
  Select,
  MenuItem,
  InputLabel,
  Stack,
  Divider,
  Button,
} from '@mui/material';
import { useTheme } from './ThemeContext';

const fontOptions = [
  { value: 'Roboto', label: 'Roboto' },
  { value: 'Open Sans', label: 'Open Sans' },
  { value: 'Lato', label: 'Lato' },
  { value: 'Poppins', label: 'Poppins' },
];

const Settings = () => {
  const {
    mode,
    setMode,
    primaryColor,
    setPrimaryColor,
    secondaryColor,
    setSecondaryColor,
    headingFont,
    setHeadingFont,
    bodyFont,
    setBodyFont,
  } = useTheme();

  return (
    <Box sx={{ width: '100%', pt: 2 }}>
      <Typography variant="h4" component="h1" sx={{ mb: 4 }}>
        Settings
      </Typography>
      <Card>
        <CardContent>
          <Stack spacing={4}>
            {/* Theme Mode */}
            <Box>
              <Typography variant="h6" sx={{ mb: 2 }}>Theme Mode</Typography>
              <FormControlLabel
                control={
                  <Switch
                    checked={mode === 'dark'}
                    onChange={(e) => setMode(e.target.checked ? 'dark' : 'light')}
                  />
                }
                label="Dark Mode"
              />
            </Box>
            <Divider />

            {/* Font Selection */}
            <Box>
              <Typography variant="h6" sx={{ mb: 2 }}>Typography</Typography>
              <Stack spacing={2}>
                <FormControl fullWidth>
                  <InputLabel>Heading Font</InputLabel>
                  <Select
                    value={headingFont}
                    label="Heading Font"
                    onChange={(e) => setHeadingFont(e.target.value)}
                  >
                    {fontOptions.map((font) => (
                      <MenuItem key={font.value} value={font.value}>
                        {font.label}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControl fullWidth>
                  <InputLabel>Body Font</InputLabel>
                  <Select
                    value={bodyFont}
                    label="Body Font"
                    onChange={(e) => setBodyFont(e.target.value)}
                  >
                    {fontOptions.map((font) => (
                      <MenuItem key={font.value} value={font.value}>
                        {font.label}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Stack>
            </Box>
            <Divider />

            {/* Color Customization */}
            <Box>
              <Typography variant="h6" sx={{ mb: 2 }}>Colors</Typography>
              <Stack spacing={2}>
                <FormControl fullWidth>
                  <InputLabel>Primary Color</InputLabel>
                  <input
                    type="color"
                    value={primaryColor}
                    onChange={(e) => setPrimaryColor(e.target.value)}
                    style={{ width: '100%', height: '40px' }}
                  />
                </FormControl>
                <FormControl fullWidth>
                  <InputLabel>Secondary Color</InputLabel>
                  <input
                    type="color"
                    value={secondaryColor}
                    onChange={(e) => setSecondaryColor(e.target.value)}
                    style={{ width: '100%', height: '40px' }}
                  />
                </FormControl>
              </Stack>
            </Box>

            {/* Reset to Defaults Button */}
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
              <Button
                variant="outlined"
                color="primary"
                onClick={() => {
                  setMode('light');
                  setPrimaryColor('#1976d2');
                  setSecondaryColor('#dc004e');
                  setHeadingFont('Roboto');
                  setBodyFont('Roboto');
                }}
              >
                Reset to Defaults
              </Button>
            </Box>

            {/* Save Button */}
            <Box sx={{ display: 'flex', justifyContent: 'flex-end' }}>
              <Button variant="contained" color="primary">
                Save Changes
              </Button>
            </Box>
          </Stack>
        </CardContent>
      </Card>
    </Box>
  );
};

export default Settings;
