                                    </Tooltip>
                                </Stack>
                                <Typography variant="body2" color="text.secondary" sx={{ mt: 1, mb: 2 }}>
                                    Upload a CSV file containing user information. The file should include headers for name, team, location, job role, email, and phone.
                                </Typography>
                                <Stack direction="row" spacing={2} alignItems="center">
                                    <input
                                        type="file"
                                        accept=".csv"
                                        onChange={handleFileUpload}
                                        style={{ display: 'none' }}
                                        ref={fileInputRef}
                                    />
                                    <Button
                                        variant="outlined"
                                        startIcon={<CloudUploadIcon />}
                                        onClick={() => fileInputRef.current?.click()}
                                        sx={{
                                            borderColor: alpha(theme.palette.divider, 0.2),
                                            color: 'text.primary',
                                            '&:hover': {
                                                borderColor: 'primary.main',
                                                bgcolor: alpha(theme.palette.primary.main, 0.04)
                                            }
                                        }}
                                    >
                                        Choose CSV File
                                    </Button>
                                    <Button
                                        variant="contained"
                                        startIcon={<SaveIcon />}
                                        onClick={handleSaveUsers}
                                        sx={{
                                            bgcolor: 'primary.main',
                                            color: 'primary.contrastText',
                                            '&:hover': {
                                                bgcolor: 'primary.dark'
                                            }
                                        }}
                                    >
                                        Save Users
                                    </Button>
                                </Stack>
                            </Box>

                            {uploadStatus && (
                                <Alert 
                                    severity={uploadStatus.severity}
                                    onClose={() => setUploadStatus(null)}
                                    sx={{ 
                                        '& .MuiAlert-message': { 
                                            color: 'text.primary' 
                                        } 
                                    }}
                                >
                                    {uploadStatus.message}
                                </Alert>
                            )}

                            <Typography variant="body2" color="text.secondary">
                                Current Users: {users.length}
                            </Typography>
                        </Stack>
                    </Box>
                </TabPanel>

                <TabPanel value={tabValue} index={2}>
                    <Box sx={{ width: '100%' }}>
                        {componentsList.map((category, index) => (
                            <React.Fragment key={category.name}>
                                <Typography
                                    variant="h6"
                                    sx={{
                                        mb: 2,
                                        mt: index > 0 ? 4 : 0,
                                        color: 'primary.main',
                                        fontWeight: 500
                                    }}
                                >
                                    {category.name}
                                </Typography>
                                <List>
                                    {category.components.map((component) => {
                                        const componentName = component.split(' - ')[0];
                                        const componentDescription = component.split(' - ')[1];
                                        const example = getComponentExample(componentName);

                                        return (
                                            <ListItem 
                                                key={component}
                                                sx={{
                                                    flexDirection: 'column',
                                                    alignItems: 'flex-start',
                                                    gap: 1
                                                }}
                                            >
                                                <Box sx={{ display: 'flex', width: '100%', mb: 1 }}>
                                                    <ListItemIcon>
                                                        <CodeIcon sx={{ color: 'text.secondary' }} />
                                                    </ListItemIcon>
                                                    <ListItemText 
                                                        primary={componentName}
                                                        secondary={componentDescription}
                                                        primaryTypographyProps={{
                                                            sx: { fontWeight: 500 }
                                                        }}
                                                    />
                                                </Box>
                                                {example && (
                                                    <Box 
                                                        sx={{ 
                                                            pl: 6,
                                                            width: '100%',
                                                            '& > *': { mb: 1 }
                                                        }}
                                                    >
                                                        {example}
                                                    </Box>
                                                )}
                                            </ListItem>
                                        );
                                    })}
                                </List>
                                {index < componentsList.length - 1 && (
                                    <Divider sx={{ my: 2 }} />
                                )}
                            </React.Fragment>
                        ))}
                    </Box>
                </TabPanel>
            </Paper>
        </Box>
    );
};

export default Settings;
