import React, { useEffect, useState } from 'react';
import Tree, { RawNodeDatum, CustomNodeElementProps } from 'react-d3-tree';
import { User } from '../types/User';
import { useUsers } from './UserContext';
import {
  Box,
  Card,
  Avatar,
  Typography,
  useTheme,
  alpha
} from '@mui/material';

interface OrgChartNodeData extends RawNodeDatum {
  attributes?: {
    name: string;
    role: string;
    imageUrl: string;
  };
}

const OrgChart: React.FC = () => {
  const theme = useTheme();
  const { users } = useUsers();
  const [treeData, setTreeData] = useState<OrgChartNodeData | null>(null);

  useEffect(() => {
    const buildTreeData = (users: User[]): OrgChartNodeData | null => {
      if (!users.length) return null;

      // Find the root (CEO or user without managerId)
      const root = users.find(user => !user.managerId);
      if (!root) return null;

      const buildNode = (user: User): OrgChartNodeData => {
        const children = users
          .filter(u => u.managerId === user.id)
          .map(child => buildNode(child));

        return {
          name: user.name,
          attributes: {
            name: user.name,
            role: user.jobRole,
            imageUrl: user.imageUrl || ''
          },
          children: children.length ? children : []
        };
      };

      return buildNode(root);
    };

    const data = buildTreeData(users);
    setTreeData(data);
  }, [users]);

  const renderCustomNode = ({ nodeDatum }: CustomNodeElementProps) => (
    <Card
      sx={{
        p: 1,
        minWidth: 200,
        bgcolor: alpha(theme.palette.background.paper, 0.9),
        boxShadow: 3,
        borderRadius: 2
      }}
    >
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
        <Avatar
          src={nodeDatum.attributes?.imageUrl}
          alt={nodeDatum.attributes?.name}
          sx={{ width: 40, height: 40 }}
        />
        <Box>
          <Typography variant="subtitle2" noWrap>
            {nodeDatum.attributes?.name}
          </Typography>
          <Typography variant="caption" color="textSecondary" noWrap>
            {nodeDatum.attributes?.role}
          </Typography>
        </Box>
      </Box>
    </Card>
  );

  if (!treeData) {
    return (
      <Box sx={{ p: 3 }}>
        <Typography>Loading organization chart...</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ width: '100%', height: '100vh' }}>
      <Tree
        data={treeData}
        renderCustomNodeElement={renderCustomNode}
        orientation="vertical"
        pathFunc="step"
        translate={{ x: window.innerWidth / 2, y: 50 }}
        separation={{ siblings: 2, nonSiblings: 2.5 }}
        zoom={0.7}
        nodeSize={{ x: 250, y: 100 }}
      />
    </Box>
  );
};

export default OrgChart;
