    useMediaQuery,
    Stack,
    Collapse,
    Tooltip,
    Accordion,
    AccordionSummary,
    AccordionDetails,
    Avatar,
    Link,
    Drawer
} from '@mui/material';
import FilterListIcon from '@mui/icons-material/FilterList';
import CloseIcon from '@mui/icons-material/Close';
import EmailIcon from '@mui/icons-material/Email';
import PhoneIcon from '@mui/icons-material/Phone';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import FileDownloadIcon from '@mui/icons-material/FileDownload';

import { User } from '../types/User';
import axios from 'axios';

const PEXELS_API_KEY = 'mIFzGvoWn6QuExlYm7tvsJDagoQLvzv9DJwiM5B2pzyaaCvPw46AFsQk';

// Function to determine likely gender based on first name
const getGenderFromName = (name: string): 'male' | 'female' => {
    const firstName = name.split(' ')[0].toLowerCase();
    // Common female name endings and specific names
    const femaleIndicators = ['a', 'na', 'ia', 'lia', 'emma', 'sarah', 'jane', 'maria', 'sophie', 'jennifer', 'anna', 'julia', 'nina', 'isabella', 'lisa'];

    // Check if the name matches any female indicators
    if (femaleIndicators.some(indicator => 
        firstName === indicator || firstName.endsWith(indicator)
    )) {
        return 'female';
    }

    return 'male'; // Default to male if no female indicators are found
};

// Helper functions to generate contact information
const getEmailFromName = (name: string): string => {
  const [firstName, lastName] = name.toLowerCase().split(' ');
  return `${firstName}.${lastName}@company.com`;
};

const getPhoneNumber = (id: number): string => {
  return `+1 (555) ${String(id).padStart(3, '0')}-${String(Math.floor(Math.random() * 10000)).padStart(4, '0')}`;
};

const getLinkedIn = (name: string): string => {
  return `linkedin.com/in/${name.toLowerCase().replace(' ', '')}`;
};

// Base user data without contact info
const baseUsers = [
  // Engineering Team
  { id: 1, name: 'John Doe', team: 'Engineering', location: 'New York', jobRole: 'Software Engineer', imageUrl: '' },
  { id: 2, name: 'Sarah Williams', team: 'Engineering', location: 'San Francisco', jobRole: 'Frontend Developer', imageUrl: '' },
  { id: 3, name: 'Emily Davis', team: 'Engineering', location: 'London', jobRole: 'Backend Developer', imageUrl: '' },
  { id: 4, name: 'Maria Garcia', team: 'Engineering', location: 'Berlin', jobRole: 'DevOps Engineer', imageUrl: '' },
  { id: 5, name: 'Alex Chen', team: 'Engineering', location: 'Singapore', jobRole: 'Software Engineer', imageUrl: '' },
  { id: 6, name: 'James Wilson', team: 'Engineering', location: 'Paris', jobRole: 'Frontend Developer', imageUrl: '' },
  { id: 31, name: 'Ryan Cooper', team: 'Engineering', location: 'Tokyo', jobRole: 'Backend Developer', imageUrl: '' },
  { id: 32, name: 'Nina Patel', team: 'Engineering', location: 'Sydney', jobRole: 'DevOps Engineer', imageUrl: '' },
  { id: 33, name: 'Marcus Wong', team: 'Engineering', location: 'New York', jobRole: 'Software Architect', imageUrl: '' },
  { id: 34, name: 'Elena Santos', team: 'Engineering', location: 'Berlin', jobRole: 'Systems Engineer', imageUrl: '' },

  // Design Team
  { id: 7, name: 'Jane Smith', team: 'Design', location: 'San Francisco', jobRole: 'UI Designer', imageUrl: '' },
  { id: 8, name: 'David Wilson', team: 'Design', location: 'New York', jobRole: 'UX Designer', imageUrl: '' },
  { id: 9, name: 'Robert Martinez', team: 'Design', location: 'Tokyo', jobRole: 'Visual Designer', imageUrl: '' },
  { id: 10, name: 'Sophie Martin', team: 'Design', location: 'Berlin', jobRole: 'UI Designer', imageUrl: '' },
  { id: 11, name: 'Emma Thompson', team: 'Design', location: 'London', jobRole: 'Product Designer', imageUrl: '' },
  { id: 35, name: 'Liam Foster', team: 'Design', location: 'Paris', jobRole: 'Motion Designer', imageUrl: '' },
  { id: 36, name: 'Yuki Tanaka', team: 'Design', location: 'Singapore', jobRole: 'Interaction Designer', imageUrl: '' },
  { id: 37, name: 'Olivia Chen', team: 'Design', location: 'Sydney', jobRole: 'Brand Designer', imageUrl: '' },
  { id: 38, name: 'Aiden Ross', team: 'Design', location: 'San Francisco', jobRole: 'Design Systems', imageUrl: '' },

  // Product Team
  { id: 12, name: 'Tom Brown', team: 'Product', location: 'Singapore', jobRole: 'Product Manager', imageUrl: '' },
  { id: 13, name: 'James Taylor', team: 'Product', location: 'Paris', jobRole: 'Product Owner', imageUrl: '' },
  { id: 14, name: 'Linda Chen', team: 'Product', location: 'Tokyo', jobRole: 'Product Manager', imageUrl: '' },
  { id: 15, name: 'Michael Scott', team: 'Product', location: 'Sydney', jobRole: 'Product Analyst', imageUrl: '' },
  { id: 39, name: 'Sofia Rodriguez', team: 'Product', location: 'London', jobRole: 'Product Strategist', imageUrl: '' },
  { id: 40, name: 'Lucas Kim', team: 'Product', location: 'New York', jobRole: 'Product Operations', imageUrl: '' },
  { id: 41, name: 'Eva Weber', team: 'Product', location: 'Berlin', jobRole: 'Technical Product Manager', imageUrl: '' },

  // Marketing Team
  { id: 16, name: 'Mike Johnson', team: 'Marketing', location: 'London', jobRole: 'Marketing Manager', imageUrl: '' },
  { id: 17, name: 'Lisa Anderson', team: 'Marketing', location: 'New York', jobRole: 'Content Strategist', imageUrl: '' },
  { id: 18, name: 'Jennifer Lee', team: 'Marketing', location: 'San Francisco', jobRole: 'Digital Marketing', imageUrl: '' },
  { id: 19, name: 'Chris Evans', team: 'Marketing', location: 'Berlin', jobRole: 'Marketing Specialist', imageUrl: '' },
  { id: 20, name: 'Anna Kim', team: 'Marketing', location: 'Singapore', jobRole: 'Content Creator', imageUrl: '' },
  { id: 42, name: 'Hugo Martinez', team: 'Marketing', location: 'Paris', jobRole: 'Brand Manager', imageUrl: '' },
  { id: 43, name: 'Zara Ali', team: 'Marketing', location: 'Tokyo', jobRole: 'Growth Manager', imageUrl: '' },
  { id: 44, name: 'Ben Thompson', team: 'Marketing', location: 'Sydney', jobRole: 'SEO Specialist', imageUrl: '' },

  // Sales Team
  { id: 21, name: 'Daniel Brown', team: 'Sales', location: 'Tokyo', jobRole: 'Sales Manager', imageUrl: '' },
  { id: 22, name: 'Rachel Green', team: 'Sales', location: 'Sydney', jobRole: 'Account Executive', imageUrl: '' },
  { id: 23, name: 'Thomas Mueller', team: 'Sales', location: 'Paris', jobRole: 'Sales Representative', imageUrl: '' },
  { id: 24, name: 'Sophia Lee', team: 'Sales', location: 'London', jobRole: 'Account Manager', imageUrl: '' },
  { id: 45, name: 'Gabriel Santos', team: 'Sales', location: 'New York', jobRole: 'Business Development', imageUrl: '' },
  { id: 46, name: 'Isabella Chen', team: 'Sales', location: 'San Francisco', jobRole: 'Sales Operations', imageUrl: '' },
  { id: 47, name: 'Max Weber', team: 'Sales', location: 'Berlin', jobRole: 'Enterprise Sales', imageUrl: '' },
  { id: 48, name: 'Lily Zhang', team: 'Sales', location: 'Singapore', jobRole: 'Solutions Consultant', imageUrl: '' },

  // Support Team
  { id: 25, name: 'Oliver Taylor', team: 'Support', location: 'Berlin', jobRole: 'Support Manager', imageUrl: '' },
  { id: 26, name: 'Isabella Garcia', team: 'Support', location: 'New York', jobRole: 'Customer Success', imageUrl: '' },
  { id: 27, name: 'William Zhang', team: 'Support', location: 'San Francisco', jobRole: 'Technical Support', imageUrl: '' },
  { id: 28, name: 'Ava Wilson', team: 'Support', location: 'Tokyo', jobRole: 'Support Specialist', imageUrl: '' },
  { id: 29, name: 'Lucas Kim', team: 'Support', location: 'Singapore', jobRole: 'Customer Success', imageUrl: '' },
  { id: 30, name: 'Mia Johnson', team: 'Support', location: 'Paris', jobRole: 'Technical Support', imageUrl: '' },
  { id: 49, name: 'Noah Parker', team: 'Support', location: 'London', jobRole: 'Implementation Specialist', imageUrl: '' },
  { id: 50, name: 'Emma Davis', team: 'Support', location: 'Sydney', jobRole: 'Customer Experience', imageUrl: '' },
  { id: 51, name: 'Leo Martinez', team: 'Support', location: 'Berlin', jobRole: 'Support Operations', imageUrl: '' },
  { id: 52, name: 'Victoria Wong', team: 'Support', location: 'Paris', jobRole: 'Technical Advisor', imageUrl: '' }
];

// Generate complete mock data with contact info
export const mockUsers: User[] = baseUsers.map(user => ({
  ...user,
  email: getEmailFromName(user.name),
  phone: getPhoneNumber(user.id),
  linkedin: getLinkedIn(user.name),
  managerId: undefined // We'll set this up for the org chart
}));

// Set up the organizational hierarchy
mockUsers.forEach(user => {
  // Set CEO as the root
  if (user.name === 'John Doe') {
    user.jobRole = 'CEO';
    user.managerId = undefined;
  }

  // Set direct reports to CEO
  if (['Sophie Martin', 'Olivia Chen', 'Tom Brown'].includes(user.name)) {
    user.managerId = mockUsers.find(u => u.name === 'John Doe')?.id;
  }

  // Engineering team hierarchy
  if (user.jobRole === 'Frontend Developer' || user.jobRole === 'Backend Developer' || 
      user.jobRole === 'DevOps Engineer' || user.jobRole === 'Software Engineer') {
    user.managerId = mockUsers.find(u => u.jobRole === 'Engineering Manager')?.id;
  }
  if (user.jobRole === 'Engineering Manager' || user.jobRole === 'Tech Lead') {
    user.managerId = mockUsers.find(u => u.jobRole === 'CTO')?.id;
  }
  if (user.jobRole === 'CTO') {
    user.managerId = mockUsers.find(u => u.jobRole === 'CEO')?.id;
  }

  // Design team hierarchy
  if (user.jobRole.includes('Designer') && !['Sophie Martin', 'Olivia Chen'].includes(user.name)) {
    user.managerId = mockUsers.find(u => u.jobRole === 'Design Manager')?.id;
  }
  if (user.jobRole === 'Design Manager') {
    user.managerId = mockUsers.find(u => u.jobRole === 'CEO')?.id;
  }

  // Product team hierarchy
  if ((user.jobRole === 'Product Manager' || user.jobRole === 'Product Owner' || 
      user.jobRole === 'Product Analyst') && user.name !== 'Tom Brown') {
    user.managerId = mockUsers.find(u => u.jobRole === 'Head of Product')?.id;
  }
  if (user.jobRole === 'Head of Product') {
    user.managerId = mockUsers.find(u => u.jobRole === 'CEO')?.id;
  }

  // Sales team hierarchy
  if (user.jobRole === 'Sales Representative' || user.jobRole === 'Solutions Consultant') {
    user.managerId = mockUsers.find(u => u.jobRole === 'Sales Manager')?.id;
  }
  if (user.jobRole === 'Sales Manager') {
    user.managerId = mockUsers.find(u => u.jobRole === 'CEO')?.id;
  }

  // Support team hierarchy
  if (user.jobRole === 'Customer Success' || user.jobRole === 'Support Engineer') {
    user.managerId = mockUsers.find(u => u.jobRole === 'Support Manager')?.id;
  }
  if (user.jobRole === 'Support Manager') {
    user.managerId = mockUsers.find(u => u.jobRole === 'CEO')?.id;
  }
});

const ITEMS_PER_PAGE = 20;

const UserList = () => {
    const [showFilters, setShowFilters] = useState(true);
    const [drawerOpen, setDrawerOpen] = useState(false);
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down('md'));
    const [users, setUsers] = useState<User[]>([]);
    const [filteredUsers, setFilteredUsers] = useState<User[]>([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [teamFilter, setTeamFilter] = useState('');
    const [locationFilter, setLocationFilter] = useState('');
    const [roleFilter, setRoleFilter] = useState('');
    const [page, setPage] = useState(1);

    const teams = Array.from(new Set(mockUsers.map(user => user.team)));
    const locations = Array.from(new Set(mockUsers.map(user => user.location)));
    const roles = Array.from(new Set(mockUsers.map(user => user.jobRole)));

    useEffect(() => {
        const fetchUserImages = async () => {
            const usersWithImages = await Promise.all(
                mockUsers.map(async (user) => {
                    try {
                        const gender = getGenderFromName(user.name);
                        const searchTerms = gender === 'female' 
                            ? [
                                'professional headshot woman business suit',
                                'corporate headshot female executive',
                                'business woman professional portrait studio',
                                'female ceo headshot professional'
                              ]
                            : [
                                'professional headshot man business suit',
                                'corporate headshot male executive',
                                'business man professional portrait studio',
                                'male ceo headshot professional'
                              ];

                        const randomTerm = searchTerms[Math.floor(Math.random() * searchTerms.length)];

                        const response = await axios.get(
                            `https://api.pexels.com/v1/search?query=${encodeURIComponent(randomTerm)}&per_page=15&orientation=portrait`,
                            {
                                headers: {
                                    'Authorization': PEXELS_API_KEY
                                }
                            }
                        );

                        const photos = response.data.photos;
                        const randomPhoto = photos[Math.floor(Math.random() * photos.length)];

                        return { 
                            ...user, 
                            imageUrl: randomPhoto?.src?.medium || 'https://via.placeholder.com/200'
                        };
                    } catch (error) {
                        console.error('Error fetching image:', error);
                        return user;
                    }
                })
            );
            setUsers(usersWithImages);
            setFilteredUsers(usersWithImages);
        };

        fetchUserImages();
    }, []);

    useEffect(() => {
        let filtered = users;

        if (searchTerm) {
            filtered = filtered.filter(user =>
                user.name.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        if (teamFilter) {
            filtered = filtered.filter(user => user.team === teamFilter);
        }

        if (locationFilter) {
            filtered = filtered.filter(user => user.location === locationFilter);
        }

        if (roleFilter) {
            filtered = filtered.filter(user => user.jobRole === roleFilter);
        }

        setFilteredUsers(filtered);
        setPage(1); // Reset to first page when filters change
    }, [searchTerm, teamFilter, locationFilter, roleFilter, users]);

    const handlePageChange = (event: React.ChangeEvent<unknown>, value: number) => {
        setPage(value);
    };

    const exportToCSV = () => {
        // Define the CSV headers
        const headers = [
            'Name',
            'Team',
            'Location',
            'Job Role',
            'Email',
            'Phone',
            'LinkedIn'
        ];

        // Convert user data to CSV format
        const csvData = filteredUsers.map(user => [
            user.name,
            user.team,
            user.location,
            user.jobRole,
            user.email,
            user.phone,
            user.linkedin
        ]);

        // Combine headers and data
        const csvContent = [
            headers.join(','),
            ...csvData.map(row => row.join(','))
        ].join('\n');

        // Create a Blob and download link
        const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
        const link = document.createElement('a');
        const url = URL.createObjectURL(blob);
        link.setAttribute('href', url);
        link.setAttribute('download', 'users.csv');
        link.style.visibility = 'hidden';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    const FilterControls = () => (
        <Stack spacing={2} sx={{ width: '100%' }}>
            <TextField
                fullWidth
                label="Search by name"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <FormControl fullWidth>
                <InputLabel>Team</InputLabel>
                <Select
                    value={teamFilter}
                    label="Team"
                    onChange={(e) => setTeamFilter(e.target.value)}
                >
                    <MenuItem value="">All</MenuItem>
                    {teams.map(team => (
                        <MenuItem key={team} value={team}>{team}</MenuItem>
                    ))}
                </Select>
            </FormControl>
            <FormControl fullWidth>
                <InputLabel>Location</InputLabel>
                <Select
                    value={locationFilter}
                    label="Location"
                    onChange={(e) => setLocationFilter(e.target.value)}
                >
                    <MenuItem value="">All</MenuItem>
                    {locations.map(location => (
                        <MenuItem key={location} value={location}>{location}</MenuItem>
                    ))}
                </Select>
            </FormControl>
            <FormControl fullWidth>
                <InputLabel>Role</InputLabel>
                <Select
                    value={roleFilter}
                    label="Role"
                    onChange={(e) => setRoleFilter(e.target.value)}
                >
                    <MenuItem value="">All</MenuItem>
                    {roles.map(role => (
                        <MenuItem key={role} value={role}>{role}</MenuItem>
                    ))}
                </Select>
            </FormControl>
            <Button
                variant="outlined"
                onClick={() => {
                    setSearchTerm('');
                    setTeamFilter('');
                    setLocationFilter('');
                    setRoleFilter('');
                }}
                disabled={!searchTerm && !teamFilter && !locationFilter && !roleFilter}
                sx={{
                    borderColor: 'divider',
                    color: 'text.secondary',
                    '&:hover': {
                        backgroundColor: 'action.hover',
                    }
                }}
            >
                Clear All
            </Button>
        </Stack>
    );

    const paginatedUsers = filteredUsers.slice(
        (page - 1) * ITEMS_PER_PAGE,
        page * ITEMS_PER_PAGE
    );

    return (
        <Box sx={{ width: '100%', pt: 2 }}>
            <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h4" component="h1">
                    Users
                </Typography>
                <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
                    {!isMobile && (
                        <Tooltip title={showFilters ? "Hide filters" : "Show filters"}>
                            <IconButton 
                                onClick={() => setShowFilters(!showFilters)}
                                size="small"
                                sx={{ 
                                    bgcolor: showFilters ? 'action.selected' : 'transparent',
                                    '&:hover': {
                                        bgcolor: showFilters ? 'action.selected' : 'action.hover'
                                    },
                                    transition: 'transform 0.2s ease-in-out',
                                    transform: showFilters ? 'rotate(-180deg)' : 'rotate(0)',
                                }}
                            >
                                <FilterListIcon />
                            </IconButton>
                        </Tooltip>
                    )}
                    {isMobile && (
                        <Button
                            variant="outlined"
                            startIcon={<FilterListIcon />}
                            onClick={() => setDrawerOpen(true)}
                        >
                            Filters
                        </Button>
                    )}
                    <Button
                        variant="outlined"
                        startIcon={<FileDownloadIcon />}
                        onClick={exportToCSV}
                        sx={{
                            borderColor: 'divider',
                            color: 'text.primary',
                            '&:hover': {
                                borderColor: 'primary.main',
                                backgroundColor: theme.palette.action.hover
                            }
                        }}
                    >
                        Export to CSV
                    </Button>
                </Box>
            </Box>
            <Box sx={{ 
                display: 'grid', 
                gridTemplateColumns: isMobile ? '1fr' : '250px 1fr',
                gap: 3,
                mb: 4 
            }}>
                {!isMobile && (
                    <Collapse in={showFilters} orientation="horizontal">
                        <Box component="aside" sx={{ position: 'sticky', top: 24, alignSelf: 'start', width: 250 }}>
                            <FilterControls />
                        </Box>
                    </Collapse>
                )}
                <Box>
                    <Grid container spacing={3}>
                        {paginatedUsers.map((user) => (
                            <Grid item xs={12} sm={6} md={4} key={user.id}>
                                <Card>
                                    <CardContent>
                                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                                            <Avatar
                                                src={user.imageUrl || 'https://via.placeholder.com/200'}
                                                alt={user.name}
                                                sx={{ width: 56, height: 56, mr: 2 }}
                                            />
                                            <Box>
                                                <Typography variant="h6" component="h2">
                                                    {user.name}
                                                </Typography>
                                                <Typography variant="body2" color="text.secondary" noWrap>
                                                    {user.jobRole}
                                                </Typography>
                                                <Typography variant="body2" color="text.secondary" noWrap>
                                                    {user.team} · {user.location}
                                                </Typography>
                                            </Box>
                                        </Box>
                                        <Accordion elevation={0} sx={{ 
                                            '&:before': {
                                                display: 'none',
                                            },
                                            boxShadow: 'none',
                                        }}>
                                            <AccordionSummary
                                                expandIcon={<ExpandMoreIcon />}
                                                aria-controls="contact-content"
                                                id="contact-header"
                                                sx={{
                                                    borderTop: '1px solid rgba(0, 0, 0, 0.12)',
                                                    minHeight: '48px',
                                                    '& .MuiAccordionSummary-content': {
                                                        margin: '12px 0',
                                                    }
                                                }}
                                            >
                                                <Typography>Contact Information</Typography>
                                            </AccordionSummary>
                                            <AccordionDetails>
                                                <Link 
                                                    href={`mailto:${user.email}`}
                                                    color="inherit"
                                                    sx={{ 
                                                        display: 'flex',
                                                        alignItems: 'center',
                                                        mb: 1,
                                                        textDecoration: 'none',
                                                        '&:hover': { textDecoration: 'underline' }
                                                    }}
                                                >
                                                    <EmailIcon fontSize="small" sx={{ mr: 1 }} />
                                                    <Typography variant="body2">{user.email}</Typography>
                                                </Link>
                                                <Link
                                                    href={`tel:${user.phone}`}
                                                    color="inherit"
                                                    sx={{
                                                        display: 'flex',
                                                        alignItems: 'center',
                                                        mb: 1,
                                                        textDecoration: 'none',
                                                        '&:hover': { textDecoration: 'underline' }
                                                    }}
                                                >
                                                    <PhoneIcon fontSize="small" sx={{ mr: 1 }} />
                                                    <Typography variant="body2">{user.phone}</Typography>
                                                </Link>
                                                <Link
                                                    href={`https://www.${user.linkedin}`}
                                                    target="_blank"
                                                    rel="noopener noreferrer"
                                                    color="inherit"
                                                    sx={{
                                                        display: 'flex',
                                                        alignItems: 'center',
                                                        textDecoration: 'none',
                                                        '&:hover': { textDecoration: 'underline' }
                                                    }}
                                                >
                                                    <LinkedInIcon fontSize="small" sx={{ mr: 1 }} />
                                                    <Typography variant="body2">{user.linkedin}</Typography>
                                                </Link>
                                            </AccordionDetails>
                                        </Accordion>
                                    </CardContent>
                                </Card>
                            </Grid>
                        ))}
                    </Grid>

                    {isMobile && (
                        <Drawer
                            anchor="right"
                            open={drawerOpen}
                            onClose={() => setDrawerOpen(false)}
                            PaperProps={{
                                sx: {
                                    width: '100%',
                                    maxWidth: '360px',
                                    p: 2,
                                }
                            }}
                        >
                            <Box sx={{ 
                                display: 'flex', 
                                alignItems: 'center', 
                                justifyContent: 'space-between',
                                mb: 2
                            }}>
                                <Typography variant="h6">Filters</Typography>
                                <IconButton onClick={() => setDrawerOpen(false)}>
                                    <CloseIcon />
                                </IconButton>
                            </Box>
                            <FilterControls />
                        </Drawer>
                    )}
                </Box>
            </Box>

            <Box sx={{ 
                mt: 2, 
                mb: 3, 
                display: 'flex', 
                alignItems: 'center',
                color: 'text.secondary',
                typography: 'body2'
            }}>
                <Typography component="span">
                    Showing {filteredUsers.length} {filteredUsers.length === 1 ? 'result' : 'results'}
                    {filteredUsers.length !== users.length && ` out of ${users.length} total users`}
                </Typography>
                {(searchTerm || teamFilter || locationFilter || roleFilter) && (
                    <Typography 
                        component="span" 
                        sx={{ 
                            ml: 1,
                            color: 'text.disabled',
                            fontSize: '0.875rem'
                        }}
                    >
                        with applied filters
                    </Typography>
                )}
            </Box>

            {filteredUsers.length > ITEMS_PER_PAGE && (
                <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center' }}>
                    <Pagination
                        count={Math.ceil(filteredUsers.length / ITEMS_PER_PAGE)}
                        page={page}
                        onChange={handlePageChange}
                        color="primary"
                    />
                </Box>
            )}
        </Box>
    );
};

export default UserList;
