import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  Container,
  useTheme,
} from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import PeopleIcon from '@mui/icons-material/People';
import AccountTreeIcon from '@mui/icons-material/AccountTree';

const Navigation: React.FC = () => {
  const theme = useTheme();
  const location = useLocation();

  const isActive = (path: string) => location.pathname === path;

  const menuItems = [
    { text: 'Dashboard', icon: <DashboardIcon />, path: '/' },
    { text: 'Users', icon: <PeopleIcon />, path: '/users' },
    { text: 'Organization', icon: <AccountTreeIcon />, path: '/org-chart' }
  ];

  return (
    <AppBar 
      position="sticky" 
      elevation={0}
      sx={{
        backgroundColor: 'background.paper',
        borderBottom: 1,
        borderColor: 'divider',
      }}
    >
      <Container maxWidth="xl">
        <Toolbar disableGutters>
          <Typography
            variant="h6"
            component="div"
            sx={{
              flexGrow: 1,
              color: 'primary.main',
              fontWeight: 700,
              fontSize: { xs: '1.1rem', sm: '1.25rem' },
            }}
          >
            Site Users Directory
          </Typography>

          <Box sx={{ display: 'flex', gap: 1 }}>
            {menuItems.map((menuItem, index) => (
              <Button
                key={index}
                component={Link}
                to={menuItem.path}
                startIcon={menuItem.icon}
                variant={isActive(menuItem.path) ? 'contained' : 'text'}
                sx={{
                  minWidth: { xs: 'auto', sm: '120px' },
                  px: { xs: 1, sm: 2 },
                  py: 1,
                  color: isActive(menuItem.path) ? 'white' : 'primary.main',
                  '&:hover': {
                    backgroundColor: isActive(menuItem.path) 
                      ? 'primary.dark'
                      : 'action.hover',
                  },
                }}
              >
                <Box sx={{ display: { xs: 'none', sm: 'block' } }}>
                  {menuItem.text}
                </Box>
              </Button>
            ))}
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default Navigation;
