import React, { useState } from 'react';
import {
    Box,
    Typography,
    Paper,
    FormControl,
    FormLabel,
    RadioGroup,
    Radio,
    FormControlLabel,
    Button,
    Stack,
    useTheme,
    alpha,
    Select,
    MenuItem,
    Tabs,
    Tab
} from '@mui/material';
import { useTheme as useThemeContext } from './ThemeContext';

interface TabPanelProps {
    children?: React.ReactNode;
    index: number;
    value: number;
}

function TabPanel(props: TabPanelProps) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`settings-tabpanel-${index}`}
            aria-labelledby={`settings-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    {children}
                </Box>
            )}
        </div>
    );
}

function a11yProps(index: number) {
    return {
        id: `settings-tab-${index}`,
        'aria-controls': `settings-tabpanel-${index}`,
    };
}

const fontOptions = [
    { value: 'Roboto', label: 'Roboto' },
    { value: 'Open Sans', label: 'Open Sans' },
    { value: 'Lato', label: 'Lato' },
    { value: 'Poppins', label: 'Poppins' },
    { value: 'Montserrat', label: 'Montserrat' },
];

const Settings: React.FC = () => {
    const [tabValue, setTabValue] = useState(0);
    const { 
        mode, 
        setMode, 
        primaryColor, 
        setPrimaryColor, 
        secondaryColor, 
        setSecondaryColor, 
        headingFont,
        setHeadingFont,
        bodyFont,
        setBodyFont,
        saveSettings,
        resetToDefaults 
    } = useThemeContext();
    const theme = useTheme();

    const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
        setTabValue(newValue);
    };

    return (
        <Box sx={{ width: '100%', p: 3 }}>
            <Typography variant="h4" component="h1" gutterBottom>
                Settings
            </Typography>
            <Paper 
                elevation={0}
                sx={{ 
                    bgcolor: 'background.paper',
                    borderRadius: 2,
                    border: `1px solid ${alpha(theme.palette.divider, 0.1)}`
                }}
            >
                <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                    <Tabs 
                        value={tabValue} 
                        onChange={handleTabChange}
                        aria-label="settings tabs"
                        sx={{
                            '& .MuiTab-root': {
                                color: 'text.secondary',
                                '&.Mui-selected': {
                                    color: 'primary.main',
                                }
                            },
                            '& .MuiTabs-indicator': {
                                backgroundColor: 'primary.main',
                            }
                        }}
                    >
                        <Tab label="Appearance" {...a11yProps(0)} />
                        <Tab label="User Management" {...a11yProps(1)} />
                    </Tabs>
                </Box>

                <TabPanel value={tabValue} index={0}>
                    <Stack spacing={4}>
                        {/* Theme Mode */}
                        <FormControl component="fieldset">
                            <FormLabel 
                                component="legend"
                                sx={{
                                    color: 'text.primary',
                                    '&.Mui-focused': {
                                        color: 'text.primary'
                                    }
                                }}
                            >
                                Theme Mode
                            </FormLabel>
                            <RadioGroup
                                row
                                value={mode}
                                onChange={(e) => setMode(e.target.value as 'light' | 'dark')}
                            >
                                <FormControlLabel 
                                    value="light" 
                                    control={
                                        <Radio 
                                            sx={{
                                                color: 'text.secondary',
                                                '&.Mui-checked': {
                                                    color: 'primary.main'
                                                }
                                            }}
                                        />
                                    } 
                                    label="Light" 
                                    sx={{
                                        color: 'text.primary'
                                    }}
                                />
                                <FormControlLabel 
                                    value="dark" 
                                    control={
                                        <Radio 
                                            sx={{
                                                color: 'text.secondary',
                                                '&.Mui-checked': {
                                                    color: 'primary.main'
                                                }
                                            }}
                                        />
                                    } 
                                    label="Dark"
                                    sx={{
                                        color: 'text.primary'
                                    }}
                                />
                            </RadioGroup>
                        </FormControl>

                        {/* Colors */}
                        <FormControl>
                            <FormLabel 
                                sx={{
                                    color: 'text.primary',
                                    '&.Mui-focused': {
                                        color: 'text.primary'
                                    },
                                    mb: 1
                                }}
                            >
                                Primary Color
                            </FormLabel>
                            <Box 
                                component="input"
                                type="color"
                                value={primaryColor}
                                onChange={(e) => setPrimaryColor(e.target.value)}
                                sx={{
                                    width: '100%',
                                    maxWidth: 200,
                                    height: 40,
                                    padding: 0,
                                    border: `1px solid ${alpha(theme.palette.divider, 0.2)}`,
                                    borderRadius: 1,
                                    bgcolor: 'background.paper',
                                    cursor: 'pointer',
                                    '&::-webkit-color-swatch-wrapper': {
                                        padding: 0,
                                    },
                                    '&::-webkit-color-swatch': {
                                        border: 'none',
                                        borderRadius: '3px'
                                    }
                                }}
                            />
                        </FormControl>

                        <FormControl>
                            <FormLabel 
                                sx={{
                                    color: 'text.primary',
                                    '&.Mui-focused': {
                                        color: 'text.primary'
                                    },
                                    mb: 1
                                }}
                            >
                                Secondary Color
                            </FormLabel>
                            <Box 
                                component="input"
                                type="color"
                                value={secondaryColor}
                                onChange={(e) => setSecondaryColor(e.target.value)}
                                sx={{
                                    width: '100%',
                                    maxWidth: 200,
                                    height: 40,
                                    padding: 0,
                                    border: `1px solid ${alpha(theme.palette.divider, 0.2)}`,
                                    borderRadius: 1,
                                    bgcolor: 'background.paper',
                                    cursor: 'pointer',
                                    '&::-webkit-color-swatch-wrapper': {
                                        padding: 0,
                                    },
                                    '&::-webkit-color-swatch': {
                                        border: 'none',
                                        borderRadius: '3px'
                                    }
                                }}
                            />
                        </FormControl>

                        {/* Typography */}
                        <FormControl>
                            <FormLabel 
                                sx={{
                                    color: 'text.primary',
                                    '&.Mui-focused': {
                                        color: 'text.primary'
                                    },
                                    mb: 1
                                }}
                            >
                                Typography
                            </FormLabel>
                            <Stack spacing={2}>
                                <Select
                                    value={headingFont}
                                    onChange={(e) => setHeadingFont(e.target.value)}
                                    sx={{
                                        bgcolor: 'background.paper',
                                        '& .MuiOutlinedInput-notchedOutline': {
                                            borderColor: alpha(theme.palette.divider, 0.2),
                                        },
                                        '&:hover .MuiOutlinedInput-notchedOutline': {
                                            borderColor: 'primary.main',
                                        },
                                    }}
                                >
                                    {fontOptions.map((font) => (
                                        <MenuItem key={font.value} value={font.value}>
                                            {font.label}
                                        </MenuItem>
                                    ))}
                                </Select>
                                <Select
                                    value={bodyFont}
                                    onChange={(e) => setBodyFont(e.target.value)}
                                    sx={{
                                        bgcolor: 'background.paper',
                                        '& .MuiOutlinedInput-notchedOutline': {
                                            borderColor: alpha(theme.palette.divider, 0.2),
                                        },
                                        '&:hover .MuiOutlinedInput-notchedOutline': {
                                            borderColor: 'primary.main',
                                        },
                                    }}
                                >
                                    {fontOptions.map((font) => (
                                        <MenuItem key={font.value} value={font.value}>
                                            {font.label}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </Stack>
                        </FormControl>

                        {/* Buttons */}
                        <Stack direction="row" spacing={2}>
                            <Button 
                                variant="outlined" 
                                onClick={resetToDefaults}
                                sx={{
                                    borderColor: alpha(theme.palette.divider, 0.2),
                                    color: 'text.primary',
                                    '&:hover': {
                                        borderColor: 'primary.main',
                                        bgcolor: alpha(theme.palette.primary.main, 0.04)
                                    }
                                }}
                            >
                                Reset to Defaults
                            </Button>
                            <Button 
                                variant="contained" 
                                onClick={saveSettings}
                                sx={{
                                    bgcolor: 'primary.main',
                                    color: 'primary.contrastText',
                                    '&:hover': {
                                        bgcolor: 'primary.dark',
                                    }
                                }}
                            >
                                Save Changes
                            </Button>
                        </Stack>
                    </Stack>
                </TabPanel>

                <TabPanel value={tabValue} index={1}>
                    <Typography variant="h5" gutterBottom>
                        User Management
                    </Typography>
                    <Box sx={{ mt: 2 }}>
                        {/* User Management content will go here */}
                    </Box>
                </TabPanel>
            </Paper>
        </Box>
    );
};

export default Settings;
