import React, { useState } from 'react';
import { Box, Grid, Paper, Typography, FormGroup, FormControlLabel, Checkbox } from '@mui/material';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import { User } from '../types/User';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

// Mock data - using the same data as UserList component
const mockUsers: User[] = [
  { id: 1, name: 'John Doe', team: 'Engineering', location: 'New York', jobRole: 'Software Engineer', imageUrl: '' },
  { id: 2, name: 'Jane Smith', team: 'Design', location: 'San Francisco', jobRole: 'UI Designer', imageUrl: '' },
  { id: 3, name: 'Mike Johnson', team: 'Marketing', location: 'London', jobRole: 'Marketing Manager', imageUrl: '' },
  { id: 4, name: 'Sarah Williams', team: 'Engineering', location: 'Berlin', jobRole: 'Frontend Developer', imageUrl: '' },
  { id: 5, name: 'Tom Brown', team: 'Product', location: 'Toronto', jobRole: 'Product Manager', imageUrl: '' },
  { id: 6, name: 'Emily Davis', team: 'Engineering', location: 'Sydney', jobRole: 'Backend Developer', imageUrl: '' },
  { id: 7, name: 'David Wilson', team: 'Design', location: 'Paris', jobRole: 'UX Designer', imageUrl: '' },
  { id: 8, name: 'Lisa Anderson', team: 'Marketing', location: 'Tokyo', jobRole: 'Content Strategist', imageUrl: '' },
  { id: 9, name: 'James Taylor', team: 'Product', location: 'Singapore', jobRole: 'Product Owner', imageUrl: '' },
  { id: 10, name: 'Maria Garcia', team: 'Engineering', location: 'Madrid', jobRole: 'DevOps Engineer', imageUrl: '' },
  { id: 11, name: 'Robert Martinez', team: 'Design', location: 'Barcelona', jobRole: 'Visual Designer', imageUrl: '' },
  { id: 12, name: 'Jennifer Lee', team: 'Marketing', location: 'Seoul', jobRole: 'Marketing Specialist', imageUrl: '' }
];

const Dashboard: React.FC = () => {
  // Process data for Roles by Location
  const rolesByLocation = mockUsers.reduce((acc: Record<string, Record<string, number>>, user: User) => {
    if (!acc[user.jobRole]) {
      acc[user.jobRole] = {};
    }
    acc[user.jobRole][user.location] = (acc[user.jobRole][user.location] || 0) + 1;
    return acc;
  }, {});

  // Process data for Roles by Team
  const rolesByTeam = mockUsers.reduce((acc: Record<string, Record<string, number>>, user: User) => {
    if (!acc[user.jobRole]) {
      acc[user.jobRole] = {};
    }
    acc[user.jobRole][user.team] = (acc[user.jobRole][user.team] || 0) + 1;
    return acc;
  }, {});

  // Process data for Teams by Location
  const teamsByLocation = mockUsers.reduce((acc: Record<string, Record<string, number>>, user: User) => {
    if (!acc[user.team]) {
      acc[user.team] = {};
    }
    acc[user.team][user.location] = (acc[user.team][user.location] || 0) + 1;
    return acc;
  }, {});

  // Add state for legend visibility
  const [roleLocationVisibility, setRoleLocationVisibility] = useState<Record<string, boolean>>({});
  const [roleTeamVisibility, setRoleTeamVisibility] = useState<Record<string, boolean>>({});
  const [teamLocationVisibility, setTeamLocationVisibility] = useState<Record<string, boolean>>({});

  // Initialize visibility state when data changes
  React.useEffect(() => {
    const initVisibility = (data: any) => 
      Object.keys(data).reduce((acc, key) => ({ ...acc, [key]: true }), {});

    setRoleLocationVisibility(initVisibility(rolesByLocation));
    setRoleTeamVisibility(initVisibility(rolesByTeam));
    setTeamLocationVisibility(initVisibility(teamsByLocation));
  }, []);

  // Generate random colors
  const generateColors = (count: number): string[] => {
    return Array.from({ length: count }, (_, i) => 
      `hsl(${(i * 360) / count}, 70%, 50%)`
    );
  };

  // Custom legend components
  const LegendCheckboxes = ({ items, visibility, setVisibility, title }: {
    items: string[];
    visibility: Record<string, boolean>;
    setVisibility: (value: Record<string, boolean>) => void;
    title: string;
  }) => (
    <Box sx={{ mt: 2 }}>
      <Typography variant="subtitle2" gutterBottom>{title}</Typography>
      <FormGroup row>
        {items.map((item) => (
          <FormControlLabel
            key={item}
            control={
              <Checkbox
                checked={visibility[item] ?? true}
                onChange={(e) => {
                  setVisibility({ ...visibility, [item]: e.target.checked });
                }}
                size="small"
              />
            }
            label={<Typography variant="caption">{item}</Typography>}
          />
        ))}
      </FormGroup>
    </Box>
  );

  // Update chart options with custom legend
  const getChartOptions = (title: string) => ({
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false, // Hide default legend
      },
      title: {
        display: true,
        text: title,
        padding: {
          bottom: 10
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 1
        }
      }
    }
  });

  // Filter datasets based on visibility
  const rolesByLocationData = {
    labels: Array.from(new Set(mockUsers.map(user => user.location))),
    datasets: Object.entries(rolesByLocation)
      .filter(([role]) => roleLocationVisibility[role] !== false)
      .map(([role, locations], index) => ({
        label: role,
        data: Object.values(locations),
        backgroundColor: generateColors(Object.keys(rolesByLocation).length)[index],
      }))
  };

  const rolesByTeamData = {
    labels: Array.from(new Set(mockUsers.map(user => user.team))),
    datasets: Object.entries(rolesByTeam)
      .filter(([role]) => roleTeamVisibility[role] !== false)
      .map(([role, teams], index) => ({
        label: role,
        data: Object.values(teams),
        backgroundColor: generateColors(Object.keys(rolesByTeam).length)[index],
      }))
  };

  const teamsByLocationData = {
    labels: Array.from(new Set(mockUsers.map(user => user.location))),
    datasets: Object.entries(teamsByLocation)
      .filter(([team]) => teamLocationVisibility[team] !== false)
      .map(([team, locations], index) => ({
        label: team,
        data: Object.values(locations),
        backgroundColor: generateColors(Object.keys(teamsByLocation).length)[index],
      }))
  };

  return (
    <Box sx={{ flexGrow: 1, p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Analytics Dashboard
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ height: 320 }}>
              <Bar data={rolesByLocationData} options={getChartOptions('Roles by Location')} />
            </Box>
            <LegendCheckboxes
              items={Object.keys(rolesByLocation)}
              visibility={roleLocationVisibility}
              setVisibility={setRoleLocationVisibility}
              title="Select Roles"
            />
          </Paper>
        </Grid>

        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ height: 320 }}>
              <Bar data={rolesByTeamData} options={getChartOptions('Roles by Team')} />
            </Box>
            <LegendCheckboxes
              items={Object.keys(rolesByTeam)}
              visibility={roleTeamVisibility}
              setVisibility={setRoleTeamVisibility}
              title="Select Roles"
            />
          </Paper>
        </Grid>

        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ height: 320 }}>
              <Bar data={teamsByLocationData} options={getChartOptions('Teams by Location')} />
            </Box>
            <LegendCheckboxes
              items={Object.keys(teamsByLocation)}
              visibility={teamLocationVisibility}
              setVisibility={setTeamLocationVisibility}
              title="Select Teams"
            />
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
