import React from 'react';
import {
    Box,
    Typography,
    Card,
    CardContent,
    useTheme,
    Avatar,
    Link,
    Stack,
    Tooltip
} from '@mui/material';
import {
    Email as EmailIcon,
    Phone as PhoneIcon,
    LinkedIn as LinkedInIcon,
    LocationOn as LocationIcon,
    Business as BusinessIcon,
} from '@mui/icons-material';
import { Tree, TreeNode } from 'react-organizational-chart';
import { User } from '../types/User';
import { styled } from '@mui/material/styles';

const StyledNode = styled('div')(({ theme }) => ({
    padding: theme.spacing(1),
    display: 'inline-block',
    '& .MuiCard-root': {
        transition: 'all 0.2s ease-in-out',
        '&:hover': {
            transform: 'translateY(-4px)',
            boxShadow: theme.shadows[8]
        }
    }
}));

interface UserProfileCardProps {
    user: User;
}

const UserProfileCard: React.FC<UserProfileCardProps> = ({ user }) => {
    const theme = useTheme();

    return (
        <Card sx={{ width: 280 }}>
            <CardContent>
                <Stack spacing={2}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <Avatar
                            src={user.imageUrl || `https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=random`}
                            alt={user.name}
                            sx={{ width: 56, height: 56 }}
                        />
                        <Box>
                            <Typography variant="h6" sx={{ fontSize: '1rem' }}>
                                {user.name}
                            </Typography>
                            <Typography color="text.secondary" variant="body2">
                                {user.jobRole}
                            </Typography>
                        </Box>
                    </Box>

                    <Stack spacing={1}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <BusinessIcon fontSize="small" color="action" />
                            <Typography variant="body2" color="text.secondary">
                                {user.team}
                            </Typography>
                        </Box>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <LocationIcon fontSize="small" color="action" />
                            <Typography variant="body2" color="text.secondary">
                                {user.location}
                            </Typography>
                        </Box>
                        <Tooltip title="Send email">
                            <Link 
                                href={`mailto:${user.email}`}
                                sx={{ 
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: 1,
                                    color: 'inherit',
                                    textDecoration: 'none',
                                    '&:hover': { color: theme.palette.primary.main }
                                }}
                            >
                                <EmailIcon fontSize="small" color="action" />
                                <Typography variant="body2" noWrap>
                                    {user.email}
                                </Typography>
                            </Link>
                        </Tooltip>
                        <Tooltip title="Call">
                            <Link 
                                href={`tel:${user.phone}`}
                                sx={{ 
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: 1,
                                    color: 'inherit',
                                    textDecoration: 'none',
                                    '&:hover': { color: theme.palette.primary.main }
                                }}
                            >
                                <PhoneIcon fontSize="small" color="action" />
                                <Typography variant="body2">
                                    {user.phone}
                                </Typography>
                            </Link>
                        </Tooltip>
                        <Tooltip title="View LinkedIn profile">
                            <Link 
                                href={`https://www.${user.linkedin}`}
                                target="_blank"
                                rel="noopener noreferrer"
                                sx={{ 
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: 1,
                                    color: 'inherit',
                                    textDecoration: 'none',
                                    '&:hover': { color: theme.palette.primary.main }
                                }}
                            >
                                <LinkedInIcon fontSize="small" color="action" />
                                <Typography variant="body2" noWrap>
                                    {user.linkedin}
                                </Typography>
                            </Link>
                        </Tooltip>
                    </Stack>
                </Stack>
            </CardContent>
        </Card>
    );
};

interface RenderTreeNodeProps {
    user: User;
    users: User[];
}

const RenderTreeNode: React.FC<RenderTreeNodeProps> = ({ user, users }) => {
    const directReports = users.filter(u => u.managerId === user.id);

    return (
        <TreeNode 
            label={
                <StyledNode>
                    <UserProfileCard user={user} />
                </StyledNode>
            }
        >
            {directReports.map((report) => (
                <RenderTreeNode key={report.id} user={report} users={users} />
            ))}
        </TreeNode>
    );
};

const OrgChart: React.FC = () => {
    const rootUser = mockUsers.find(user => !user.managerId);

    if (!rootUser) {
        return (
            <Typography color="error">
                No root user found in the organization
            </Typography>
        );
    }

    return (
        <Box sx={{ 
            width: '100%', 
            pt: 2,
            overflowX: 'auto'
        }}>
            <Typography variant="h4" component="h1" sx={{ mb: 4 }}>
                Organization Chart
            </Typography>
            <Box sx={{ 
                minHeight: 'calc(100vh - 180px)',
                display: 'flex',
                justifyContent: 'center',
                p: 4
            }}>
                <Tree
                    lineWidth="2px"
                    lineColor="#bbb"
                    lineBorderRadius="10px"
                    label={
                        <StyledNode>
                            <UserProfileCard user={rootUser} />
                        </StyledNode>
                    }
                >
                    {mockUsers
                        .filter(user => user.managerId === rootUser.id)
                        .map(user => (
                            <RenderTreeNode key={user.id} user={user} users={mockUsers} />
                        ))
                    }
                </Tree>
            </Box>
        </Box>
    );
};

const mockUsers: User[] = [
    {
        id: 1,
        name: 'John Doe',
