        team: 'Operations',
        location: 'Chicago',
        jobRole: 'COO',
        imageUrl: '',
        email: 'michael.brown@company.com',
        phone: '+1 (555) 000-0003',
        linkedin: 'linkedin.com/in/michaelbrown',
        managerId: 1,
        directReports: [6, 7]
    },
    {
        id: 4,
        name: 'Emily Davis',
        team: 'Engineering',
        location: 'Seattle',
        jobRole: 'Engineering Manager',
        imageUrl: '',
        email: 'emily.davis@company.com',
        phone: '+1 (555) 000-0004',
        linkedin: 'linkedin.com/in/emilydavis',
        managerId: 2
    },
    {
        id: 5,
        name: 'David Wilson',
        team: 'Engineering',
        location: 'Boston',
        jobRole: 'Tech Lead',
        imageUrl: '',
        email: 'david.wilson@company.com',
        phone: '+1 (555) 000-0005',
        linkedin: 'linkedin.com/in/davidwilson',
        managerId: 2
    },
    {
        id: 6,
        name: 'Lisa Anderson',
        team: 'Operations',
        location: 'Miami',
        jobRole: 'Operations Manager',
        imageUrl: '',
        email: 'lisa.anderson@company.com',
        phone: '+1 (555) 000-0006',
        linkedin: 'linkedin.com/in/lisaanderson',
        managerId: 3
    },
    {
        id: 7,
