import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider as MuiThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import { useMemo } from 'react';

import Navigation from './components/Navigation';
import UserList from './components/UserList';
import Dashboard from './components/Dashboard';
import OrgChart from './components/OrgChart';
import Settings from './components/Settings';
import SkillsMatrix from './components/SkillsMatrix';
import { ThemeProvider as CustomThemeProvider, useTheme } from './components/ThemeContext';
import { UserProvider } from './components/UserContext';

function ThemedApp() {
  const {
    mode,
    primaryColor,
    secondaryColor,
    headingFont,
    bodyFont,
  } = useTheme();

  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode,
          primary: {
            main: primaryColor,
          },
          secondary: {
            main: secondaryColor,
          },
          background: {
            default: mode === 'light' ? '#f5f8fa' : '#121212',
            paper: mode === 'light' ? '#ffffff' : '#1e1e1e',
          },
        },
        typography: {
          fontFamily: bodyFont,
          h1: { fontFamily: headingFont },
          h2: { fontFamily: headingFont },
          h3: { fontFamily: headingFont },
          h4: { fontFamily: headingFont },
          h5: { fontFamily: headingFont },
          h6: { fontFamily: headingFont },
        },
        components: {
          MuiAppBar: {
            styleOverrides: {
              root: {
                backgroundColor: primaryColor,
                color: '#ffffff',
                '& .MuiIconButton-root': {
                  color: '#ffffff',
                },
                '& .MuiTypography-root': {
                  color: '#ffffff',
                },
              },
            },
          },
          MuiButton: {
            styleOverrides: {
              root: {
                borderRadius: 8,
                textTransform: 'none',
              },
            },
          },
          MuiCard: {
            styleOverrides: {
              root: {
                borderRadius: 12,
                boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
              },
            },
          },
          MuiPaper: {
            styleOverrides: {
              root: {
                borderRadius: 12,
              },
            },
          },
        },
      }),
    [mode, primaryColor, secondaryColor, headingFont, bodyFont]
  );

  return (
    <MuiThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ display: 'flex' }}>
        <Router>
          <Navigation />
          <Box
            component="main"
            sx={{
              flexGrow: 1,
              minHeight: '100vh',
              bgcolor: 'background.default',
              mt: ['48px', '56px', '64px'],
              overflow: 'auto'
            }}
          >
            <Routes>
              <Route path="/" element={<Dashboard />} />
              <Route path="/users" element={<UserList />} />
              <Route path="/org-chart" element={<OrgChart />} />
              <Route path="/skills-matrix" element={<SkillsMatrix />} />
              <Route path="/settings" element={<Settings />} />
            </Routes>
          </Box>
        </Router>
      </Box>
    </MuiThemeProvider>
  );
}

function App() {
  return (
    <UserProvider>
      <CustomThemeProvider>
        <ThemedApp />
      </CustomThemeProvider>
    </UserProvider>
  );
}

export default App;
