import React, { useState, useRef } from 'react';
import {
    Box,
    Typography,
    Paper,
    FormControl,
    FormLabel,
    RadioGroup,
    Radio,
    FormControlLabel,
    Button,
    Stack,
    useTheme,
    alpha,
    Select,
    MenuItem,
    Tabs,
    Tab,
    Alert,
    IconButton,
    Tooltip
} from '@mui/material';
import { useTheme as useThemeContext } from './ThemeContext';
import { useUsers } from './UserContext';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';
import SaveIcon from '@mui/icons-material/Save';
import { User } from '../types/User';

interface TabPanelProps {
    children?: React.ReactNode;
    index: number;
    value: number;
}

function TabPanel(props: TabPanelProps) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`settings-tabpanel-${index}`}
            aria-labelledby={`settings-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    {children}
                </Box>
            )}
        </div>
    );
}

function a11yProps(index: number) {
    return {
        id: `settings-tab-${index}`,
        'aria-controls': `settings-tabpanel-${index}`,
    };
}

const fontOptions = [
    { value: 'Roboto', label: 'Roboto' },
    { value: 'Open Sans', label: 'Open Sans' },
    { value: 'Lato', label: 'Lato' },
    { value: 'Poppins', label: 'Poppins' },
    { value: 'Montserrat', label: 'Montserrat' },
];

const fontWeightOptions = [
    { value: 300, label: 'Light' },
    { value: 400, label: 'Regular' },
    { value: 500, label: 'Medium' },
    { value: 600, label: 'Semi Bold' },
    { value: 700, label: 'Bold' },
    { value: 800, label: 'Extra Bold' }
];

const Settings: React.FC = () => {
    const [tabValue, setTabValue] = useState(0);
    const [uploadStatus, setUploadStatus] = useState<{
        message: string;
        severity: 'success' | 'error' | 'info';
    } | null>(null);
    const { users, addUsers } = useUsers();
    const fileInputRef = useRef<HTMLInputElement>(null);
    const { 
        mode, 
        setMode, 
        primaryColor, 
        setPrimaryColor, 
        secondaryColor, 
        setSecondaryColor, 
        headingFont,
        setHeadingFont,
        bodyFont,
        setBodyFont,
        headingFontWeights,
        setHeadingFontWeights,
        saveSettings,
        resetToDefaults 
    } = useThemeContext();
    const theme = useTheme();

    // Helper functions for generating user data
    const getGenderFromName = (name: string): 'male' | 'female' => {
        const firstNameLower = name.split(' ')[0].toLowerCase();
        const femaleNames = ['sarah', 'emily', 'maria', 'sophia', 'olivia', 'emma', 'isabella', 'lily'];
        return femaleNames.includes(firstNameLower) ? 'female' : 'male';
    };

    const getImageUrl = (name: string, gender: 'male' | 'female'): string => {
        return `https://xsgames.co/randomusers/avatar.php?g=${gender}`;
    };

    const getEmailFromName = (name: string): string => {
        return name.toLowerCase().replace(' ', '.') + '@company.com';
    };

    const getPhoneNumber = (id: number): string => {
        return `+1 (555) ${String(id).padStart(3, '0')}-${String(Math.floor(Math.random() * 10000)).padStart(4, '0')}`;
    };

    const getLinkedIn = (name: string): string => {
        return `https://linkedin.com/in/${name.toLowerCase().replace(' ', '-')}`;
    };

    const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
        setTabValue(newValue);
    };

    const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
        const file = event.target.files?.[0];
        if (!file) return;

        const reader = new FileReader();
        reader.onload = (e) => {
            try {
                const text = e.target?.result as string;
                const lines = text.split('\n');
                const headers = lines[0].split(',').map(header => header.trim());

                // Validate headers
                const requiredHeaders = ['name', 'team', 'location', 'jobRole', 'email', 'phone'];
                const missingHeaders = requiredHeaders.filter(header => !headers.includes(header));

                if (missingHeaders.length > 0) {
                    setUploadStatus({
                        message: `Missing required headers: ${missingHeaders.join(', ')}`,
                        severity: 'error'
                    });
                    return;
                }

                // Parse users
                const newUsers = lines.slice(1)
                    .filter(line => line.trim())
                    .map((line, index) => {
                        const values = line.split(',').map(value => value.trim());
                        const name = values[headers.indexOf('name')];
                        const gender = getGenderFromName(name);
                        const managerName = headers.includes('manager') ? values[headers.indexOf('manager')] : '';

                        const user: User = {
                            id: Date.now() + index,
                            name: '',
                            team: '',
                            location: '',
                            jobRole: '',
                            email: '',
                            phone: '',
                            imageUrl: getImageUrl(name, gender),
                            linkedin: getLinkedIn(name),
                            managerId: undefined // Will be set after all users are processed
                        };

                        headers.forEach((header, i) => {
                            if (values[i] && header in user && header !== 'manager') {
                                (user as any)[header] = values[i];
                            }
                        });

                        // Set default values for missing fields
                        if (!user.email) user.email = getEmailFromName(user.name);
                        if (!user.phone) user.phone = getPhoneNumber(user.id);

                        return { user, managerName };
                    });

                // Process manager relationships
                const processedUsers = newUsers.map(({ user, managerName }) => {
                    if (managerName) {
                        // First look in existing users
                        const existingManager = users.find(u => u.name === managerName);
                        if (existingManager) {
                            user.managerId = existingManager.id;
                        } else {
                            // Then look in newly added users
                            const newManager = newUsers.find(u => u.user.name === managerName);
                            if (newManager) {
                                user.managerId = newManager.user.id;
                            }
                        }
                    }
                    return user;
                });

                // Add new users
                addUsers(processedUsers);

                setUploadStatus({
                    message: `Successfully added ${processedUsers.length} users`,
                    severity: 'success'
                });

                // Reset file input
                if (fileInputRef.current) {
                    fileInputRef.current.value = '';
                }
            } catch (error) {
                setUploadStatus({
                    message: 'Error parsing CSV file. Please check the format.',
                    severity: 'error'
                });
            }
        };

        reader.onerror = () => {
            setUploadStatus({
                message: 'Error reading file',
                severity: 'error'
            });
        };

        reader.readAsText(file);
    };

    const handleSaveUsers = () => {
        try {
            localStorage.setItem('users', JSON.stringify(users));
            setUploadStatus({
                message: 'Users saved successfully',
                severity: 'success'
            });
        } catch (error) {
            setUploadStatus({
                message: 'Error saving users',
                severity: 'error'
            });
        }
    };

    const handleFontWeightChange = (heading: string, weight: number) => {
        setHeadingFontWeights((prev: { [key: string]: number }) => ({
            ...prev,
            [heading]: weight
        }));
    };

    return (
        <Box sx={{ width: '100%', p: 3 }}>
            <Typography variant="h4" component="h1" gutterBottom>
                Settings
            </Typography>
            <Paper 
                elevation={0}
                sx={{ 
                    bgcolor: 'background.paper',
                    borderRadius: 2,
                    border: `1px solid ${alpha(theme.palette.divider, 0.1)}`
                }}
            >
                <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                    <Tabs 
                        value={tabValue} 
                        onChange={handleTabChange}
                        aria-label="settings tabs"
                        sx={{
                            '& .MuiTab-root': {
                                color: 'text.secondary',
                                '&.Mui-selected': {
                                    color: 'primary.main',
                                }
                            },
                            '& .MuiTabs-indicator': {
                                backgroundColor: 'primary.main',
                            }
                        }}
                    >
                        <Tab label="Appearance" {...a11yProps(0)} />
                        <Tab label="User Management" {...a11yProps(1)} />
                    </Tabs>
                </Box>

                <TabPanel value={tabValue} index={0}>
                    <Stack spacing={4}>
                        {/* Theme Mode */}
                        <FormControl component="fieldset">
                            <FormLabel 
                                component="legend"
                                sx={{
                                    color: 'text.primary',
                                    '&.Mui-focused': {
                                        color: 'text.primary'
                                    }
                                }}
                            >
                                Theme Mode
                            </FormLabel>
                            <RadioGroup
                                row
                                value={mode}
                                onChange={(e) => setMode(e.target.value as 'light' | 'dark')}
                            >
                                <FormControlLabel 
                                    value="light" 
                                    control={
                                        <Radio 
                                            sx={{
                                                color: 'text.secondary',
                                                '&.Mui-checked': {
                                                    color: 'primary.main'
                                                }
                                            }}
                                        />
                                    } 
                                    label="Light" 
                                    sx={{
                                        color: 'text.primary'
                                    }}
                                />
                                <FormControlLabel 
                                    value="dark" 
                                    control={
                                        <Radio 
                                            sx={{
                                                color: 'text.secondary',
                                                '&.Mui-checked': {
                                                    color: 'primary.main'
                                                }
                                            }}
                                        />
                                    } 
                                    label="Dark"
                                    sx={{
                                        color: 'text.primary'
                                    }}
                                />
                            </RadioGroup>
                        </FormControl>

                        {/* Colors */}
                        <FormControl>
                            <FormLabel 
                                sx={{
                                    color: 'text.primary',
                                    '&.Mui-focused': {
                                        color: 'text.primary'
                                    },
                                    mb: 1
                                }}
                            >
                                Primary Color
                            </FormLabel>
                            <Box 
                                component="input"
                                type="color"
                                value={primaryColor}
                                onChange={(e) => setPrimaryColor(e.target.value)}
                                sx={{
                                    width: '100%',
                                    maxWidth: 200,
                                    height: 40,
                                    padding: 0,
                                    border: `1px solid ${alpha(theme.palette.divider, 0.2)}`,
                                    borderRadius: 1,
                                    bgcolor: 'background.paper',
                                    cursor: 'pointer',
                                    '&::-webkit-color-swatch-wrapper': {
                                        padding: 0,
                                    },
                                    '&::-webkit-color-swatch': {
                                        border: 'none',
                                        borderRadius: '3px'
                                    }
                                }}
                            />
                        </FormControl>

                        <FormControl>
                            <FormLabel 
                                sx={{
                                    color: 'text.primary',
                                    '&.Mui-focused': {
                                        color: 'text.primary'
                                    },
                                    mb: 1
                                }}
                            >
                                Secondary Color
                            </FormLabel>
                            <Box 
                                component="input"
                                type="color"
                                value={secondaryColor}
                                onChange={(e) => setSecondaryColor(e.target.value)}
                                sx={{
                                    width: '100%',
                                    maxWidth: 200,
                                    height: 40,
                                    padding: 0,
                                    border: `1px solid ${alpha(theme.palette.divider, 0.2)}`,
                                    borderRadius: 1,
                                    bgcolor: 'background.paper',
                                    cursor: 'pointer',
                                    '&::-webkit-color-swatch-wrapper': {
                                        padding: 0,
                                    },
                                    '&::-webkit-color-swatch': {
                                        border: 'none',
                                        borderRadius: '3px'
                                    }
                                }}
                            />
                        </FormControl>

                        {/* Typography */}
                        <FormControl>
                            <FormLabel 
                                sx={{
                                    color: 'text.primary',
                                    '&.Mui-focused': {
                                        color: 'text.primary'
                                    },
                                    mb: 1
                                }}
                            >
                                Typography
                            </FormLabel>
                            <Stack spacing={3}>
                                <FormControl fullWidth>
                                    <FormLabel>Heading Font</FormLabel>
                                    <Select
                                        value={headingFont}
                                        onChange={(e) => setHeadingFont(e.target.value)}
                                        sx={{
                                            bgcolor: 'background.paper',
                                            '& .MuiOutlinedInput-notchedOutline': {
                                                borderColor: alpha(theme.palette.divider, 0.2),
                                            },
                                            '&:hover .MuiOutlinedInput-notchedOutline': {
                                                borderColor: 'primary.main',
                                            },
                                        }}
                                    >
                                        {fontOptions.map((font) => (
                                            <MenuItem key={font.value} value={font.value}>
                                                {font.label}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>

                                <Box>
                                    <Typography variant="subtitle1" gutterBottom>Heading Font Weights</Typography>
                                    <Stack spacing={2}>
                                        {['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].map((heading) => (
                                            <FormControl key={heading} fullWidth>
                                                <FormLabel>{heading.toUpperCase()}</FormLabel>
                                                <Select
                                                    value={headingFontWeights[heading]}
                                                    onChange={(e) => handleFontWeightChange(heading, Number(e.target.value))}
                                                    size="small"
                                                    sx={{
                                                        bgcolor: 'background.paper',
                                                        '& .MuiOutlinedInput-notchedOutline': {
                                                            borderColor: alpha(theme.palette.divider, 0.2),
                                                        },
                                                        '&:hover .MuiOutlinedInput-notchedOutline': {
                                                            borderColor: 'primary.main',
                                                        },
                                                    }}
                                                >
                                                    {fontWeightOptions.map((weight) => (
                                                        <MenuItem key={weight.value} value={weight.value}>
                                                            {weight.label} ({weight.value})
                                                        </MenuItem>
                                                    ))}
                                                </Select>
                                            </FormControl>
                                        ))}
                                    </Stack>
                                </Box>

                                <FormControl fullWidth>
                                    <FormLabel>Body Font</FormLabel>
                                    <Select
                                        value={bodyFont}
                                        onChange={(e) => setBodyFont(e.target.value)}
                                        sx={{
                                            bgcolor: 'background.paper',
                                            '& .MuiOutlinedInput-notchedOutline': {
                                                borderColor: alpha(theme.palette.divider, 0.2),
                                            },
                                            '&:hover .MuiOutlinedInput-notchedOutline': {
                                                borderColor: 'primary.main',
                                            },
                                        }}
                                    >
                                        {fontOptions.map((font) => (
                                            <MenuItem key={font.value} value={font.value}>
                                                {font.label}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                            </Stack>
                        </FormControl>

                        {/* Buttons */}
                        <Stack direction="row" spacing={2}>
                            <Button 
                                variant="outlined" 
                                onClick={resetToDefaults}
                                sx={{
                                    borderColor: alpha(theme.palette.divider, 0.2),
                                    color: 'text.primary',
                                    '&:hover': {
                                        borderColor: 'primary.main',
                                        bgcolor: alpha(theme.palette.primary.main, 0.04)
                                    }
                                }}
                            >
                                Reset to Defaults
                            </Button>
                            <Button 
                                variant="contained" 
                                onClick={saveSettings}
                                sx={{
                                    bgcolor: 'primary.main',
                                    color: 'primary.contrastText',
                                    '&:hover': {
                                        bgcolor: 'primary.dark',
                                    }
                                }}
                            >
                                Save Changes
                            </Button>
                        </Stack>
                    </Stack>
                </TabPanel>

                <TabPanel value={tabValue} index={1}>
                    <Typography variant="h5" gutterBottom>
                        User Management
                    </Typography>
                    <Box sx={{ mt: 2 }}>
                        <Stack spacing={3}>
                            <Box>
                                <Stack direction="row" spacing={1} alignItems="center">
                                    <Typography variant="h6">
                                        Import Users
                                    </Typography>
                                    <Tooltip title="Upload a CSV file with the following headers: name, team, location, jobRole, email, phone">
                                        <IconButton size="small">
                                            <HelpOutlineIcon />
                                        </IconButton>
                                    </Tooltip>
                                </Stack>
                                <Typography variant="body2" color="text.secondary" sx={{ mt: 1, mb: 2 }}>
                                    Upload a CSV file containing user information. The file should include headers for name, team, location, job role, email, and phone.
                                </Typography>
                                <Stack direction="row" spacing={2} alignItems="center">
                                    <input
                                        type="file"
                                        accept=".csv"
                                        onChange={handleFileUpload}
                                        style={{ display: 'none' }}
                                        ref={fileInputRef}
                                    />
                                    <Button
                                        variant="outlined"
                                        startIcon={<CloudUploadIcon />}
                                        onClick={() => fileInputRef.current?.click()}
                                        sx={{
                                            borderColor: alpha(theme.palette.divider, 0.2),
                                            color: 'text.primary',
                                            '&:hover': {
                                                borderColor: 'primary.main',
                                                bgcolor: alpha(theme.palette.primary.main, 0.04)
                                            }
                                        }}
                                    >
                                        Choose CSV File
                                    </Button>
                                    <Button
                                        variant="contained"
                                        startIcon={<SaveIcon />}
                                        onClick={handleSaveUsers}
                                        sx={{
                                            bgcolor: 'primary.main',
                                            color: 'primary.contrastText',
                                            '&:hover': {
                                                bgcolor: 'primary.dark'
                                            }
                                        }}
                                    >
                                        Save Users
                                    </Button>
                                </Stack>
                            </Box>

                            {uploadStatus && (
                                <Alert 
                                    severity={uploadStatus.severity}
                                    onClose={() => setUploadStatus(null)}
                                    sx={{ 
                                        '& .MuiAlert-message': { 
                                            color: 'text.primary' 
                                        } 
                                    }}
                                >
                                    {uploadStatus.message}
                                </Alert>
                            )}

                            <Typography variant="body2" color="text.secondary">
                                Current Users: {users.length}
                            </Typography>
                        </Stack>
                    </Box>
                </TabPanel>
            </Paper>
        </Box>
    );
};

export default Settings;
