import React, { useState, useEffect } from 'react';
import {
    Box,
    Typography,
    Card,
    CardContent,
    useTheme,
    Avatar,
    Stack,
    Paper,
    Dialog,
    Link,
    Accordion,
    AccordionSummary,
    AccordionDetails
} from '@mui/material';
import { 
    Email as EmailIcon, 
    Phone as PhoneIcon, 
    LinkedIn as LinkedInIcon,
    ExpandMore as ExpandMoreIcon 
} from '@mui/icons-material';
import Tree, { RawNodeDatum } from 'react-d3-tree';
import { User } from '../types/User';
import { mockUsers } from './UserList';
import axios from 'axios';

const PEXELS_API_KEY = 'YOUR_PEXELS_API_KEY'; // replace with your actual API key

interface TreeData extends RawNodeDatum {
    name: string;
    attributes?: {
        id?: string;
        imageUrl?: string;
        jobRole?: string;
        team?: string;
    };
    children?: TreeData[];
}

interface NodeRendererProps {
    nodeDatum: TreeData;
}

const getGenderFromName = (name: string) => {
    // implement your logic to determine the gender from the name
    // for simplicity, this example assumes the first name is in the format 'male-first-name' or 'female-first-name'
    const firstName = name.split(' ')[0];
    return firstName.startsWith('male-') ? 'male' : 'female';
};

const UserProfileCard: React.FC<{ user: User, onClick?: () => void }> = ({ user, onClick }) => {
    const theme = useTheme();

    return (
        <Card 
            sx={{ 
                borderRadius: '12px',
                boxShadow: 4,
                cursor: 'pointer',
                '&:hover': {
                    boxShadow: 6
                }
            }}
            onClick={onClick}
        >
            <CardContent sx={{ textAlign: 'center', p: 2 }}>
                <Stack spacing={1} alignItems="center">
                    <Avatar
                        src={user.imageUrl || `https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=${encodeURIComponent(theme.palette.primary.main)}&color=${encodeURIComponent(theme.palette.common.white)}`}
                        alt={user.name}
                        sx={{ 
                            width: 64, 
                            height: 64,
                            mb: 1,
                            bgcolor: theme.palette.primary.main,
                            color: theme.palette.common.white,
                            border: `2px solid ${theme.palette.primary.main}`
                        }}
                    />
                    <Typography variant="h6" sx={{ 
                        fontSize: '1rem',
                        fontWeight: 600,
                        lineHeight: 1.2
                    }}>
                        {user.name}
                    </Typography>
                    <Typography 
                        color="text.secondary" 
                        variant="body2"
                        sx={{ 
                            fontSize: '0.875rem',
                            lineHeight: 1.2
                        }}
                    >
                        {user.jobRole}
                    </Typography>
                    <Typography
                        variant="caption"
                        sx={{
                            fontSize: '0.75rem',
                            lineHeight: 1.2,
                            color: theme.palette.primary.main,
                            bgcolor: theme.palette.primary.main + '10',
                            px: 1,
                            py: 0.5,
                            borderRadius: '4px',
                            fontWeight: 500
                        }}
                    >
                        {user.team}
                    </Typography>
                </Stack>
            </CardContent>
        </Card>
    );
};

const UserDialog = ({ user, open, onClose }: { user: User, open: boolean, onClose: () => void }) => {
    const theme = useTheme();
    const [imageUrl, setImageUrl] = useState<string>(user.imageUrl);

    useEffect(() => {
        const fetchUserImage = async () => {
            if (!user.imageUrl) {
                try {
                    const gender = getGenderFromName(user.name);
                    const searchTerms = gender === 'female' 
                        ? [
                            'professional headshot woman business suit',
                            'corporate headshot female executive',
                            'business woman professional portrait studio',
                            'female ceo headshot professional'
                          ]
                        : [
                            'professional headshot man business suit',
                            'corporate headshot male executive',
                            'business man professional portrait studio',
                            'male ceo headshot professional'
                          ];

                    const randomTerm = searchTerms[Math.floor(Math.random() * searchTerms.length)];

                    const response = await axios.get(
                        `https://api.pexels.com/v1/search?query=${encodeURIComponent(randomTerm)}&per_page=15&orientation=portrait`,
                        {
                            headers: {
                                'Authorization': PEXELS_API_KEY
                            }
                        }
                    );

                    const photos = response.data.photos;
                    const randomPhoto = photos[Math.floor(Math.random() * photos.length)];
                    setImageUrl(randomPhoto?.src?.medium || 'https://via.placeholder.com/200');
                } catch (error) {
                    console.error('Error fetching image:', error);
                    setImageUrl('https://via.placeholder.com/200');
                }
            }
        };

        fetchUserImage();
    }, [user.name, user.imageUrl]);

    return (
        <Dialog 
            open={open} 
            onClose={onClose}
            maxWidth="sm"
            fullWidth
        >
            <Card>
                <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <Avatar
                            src={imageUrl}
                            alt={user.name}
                            sx={{ 
                                width: 56, 
                                height: 56, 
                                mr: 2,
                                bgcolor: theme.palette.primary.main,
                                color: 'white'
                            }}
                        >
                            {user.name.charAt(0)}
                        </Avatar>
                        <Box>
                            <Typography variant="h6" component="h2">
                                {user.name}
                            </Typography>
                            <Typography variant="body2" color="text.secondary" noWrap>
                                {user.jobRole}
                            </Typography>
                            <Typography variant="body2" color="text.secondary" noWrap>
                                {user.team} · {user.location}
                            </Typography>
                        </Box>
                    </Box>
                    <Accordion 
                        elevation={0} 
                        sx={{ 
                            '&:before': {
                                display: 'none',
                            },
                            boxShadow: 'none',
                        }}
                    >
                        <AccordionSummary
                            expandIcon={<ExpandMoreIcon />}
                            aria-controls="contact-content"
                            id="contact-header"
                            sx={{
                                borderTop: '1px solid rgba(0, 0, 0, 0.12)',
                                minHeight: '48px',
                                '& .MuiAccordionSummary-content': {
                                    margin: '12px 0',
                                }
                            }}
                        >
                            <Typography>Contact Information</Typography>
                        </AccordionSummary>
                        <AccordionDetails>
                            <Link 
                                href={`mailto:${user.email}`}
                                color="inherit"
                                sx={{ 
                                    display: 'flex',
                                    alignItems: 'center',
                                    mb: 1,
                                    textDecoration: 'none',
                                    '&:hover': { textDecoration: 'underline' }
                                }}
                            >
                                <EmailIcon fontSize="small" sx={{ mr: 1 }} />
                                <Typography variant="body2">{user.email}</Typography>
                            </Link>
                            <Link
                                href={`tel:${user.phone}`}
                                color="inherit"
                                sx={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    mb: 1,
                                    textDecoration: 'none',
                                    '&:hover': { textDecoration: 'underline' }
                                }}
                            >
                                <PhoneIcon fontSize="small" sx={{ mr: 1 }} />
                                <Typography variant="body2">{user.phone}</Typography>
                            </Link>
                            <Link
                                href={`https://www.${user.linkedin}`}
                                target="_blank"
                                rel="noopener noreferrer"
                                color="inherit"
                                sx={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    textDecoration: 'none',
                                    '&:hover': { textDecoration: 'underline' }
                                }}
                            >
                                <LinkedInIcon fontSize="small" sx={{ mr: 1 }} />
                                <Typography variant="body2">{user.linkedin}</Typography>
                            </Link>
                        </AccordionDetails>
                    </Accordion>
                </CardContent>
            </Card>
        </Dialog>
    );
};

const buildTreeData = (users: User[]): TreeData => {
    const buildNode = (user: User): TreeData => {
        const directReports = users.filter(u => u.managerId === user.id);
        return {
            name: user.name,
            attributes: {
                id: user.id.toString(),
                imageUrl: user.imageUrl,
                jobRole: user.jobRole,
                team: user.team
            },
            children: directReports.map(report => buildNode(report))
        };
    };

    const rootUser = users.find(user => !user.managerId);
    return rootUser ? buildNode(rootUser) : { name: 'No Data', attributes: { id: '0' } };
};

const OrgChart: React.FC = () => {
    const theme = useTheme();
    const [selectedUser, setSelectedUser] = useState<User | null>(null);
    const treeData = buildTreeData(mockUsers);

    const renderForeignObjectNode = ({ nodeDatum }: NodeRendererProps) => {
        const user = {
            id: parseInt(nodeDatum.attributes?.id || '0'),
            name: nodeDatum.name,
            imageUrl: nodeDatum.attributes?.imageUrl || '',
            jobRole: nodeDatum.attributes?.jobRole || '',
            team: nodeDatum.attributes?.team || '',
            location: '',
            email: '',
            phone: '',
            linkedin: '',
            managerId: undefined
        };

        return (
            <g>
                <foreignObject x={-120} y={-50} width={240} height={220}>
                    <UserProfileCard 
                        user={user}
                        onClick={() => setSelectedUser(mockUsers.find(u => u.id === user.id) || null)}
                    />
                </foreignObject>
            </g>
        );
    };

    return (
        <Paper 
            elevation={0} 
            sx={{ 
                height: 'calc(100vh - 100px)',
                bgcolor: 'background.default'
            }}
        >
            <Box sx={{ width: '100%', height: '100%' }}>
                <Tree
                    data={treeData}
                    renderCustomNodeElement={renderForeignObjectNode}
                    orientation="vertical"
                    pathFunc="step"
                    translate={{ x: window.innerWidth / 3, y: 80 }}
                    separation={{ siblings: 2.5, nonSiblings: 3 }}
                    zoom={0.7}
                    nodeSize={{ x: 240, y: 220 }}
                />
            </Box>
            {selectedUser && (
                <UserDialog
                    user={selectedUser}
                    open={Boolean(selectedUser)}
                    onClose={() => setSelectedUser(null)}
                />
            )}
        </Paper>
    );
};

export default OrgChart;
