import React, { useState } from 'react';
import { Box, Grid, Paper, Typography, FormControl, InputLabel, Select, MenuItem, OutlinedInput, Chip, Theme, useTheme, Button } from '@mui/material';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import { User } from '../types/User';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

// Mock data - using expanded dataset for better visualization
const mockUsers: User[] = [
  // Engineering Team
  { id: 1, name: 'John Doe', team: 'Engineering', location: 'New York', jobRole: 'Software Engineer', imageUrl: '' },
