  { id: 38, name: 'Aiden Ross', team: 'Design', location: 'San Francisco', jobRole: 'Design Systems', imageUrl: '' },

  // Product Team
  { id: 12, name: 'Tom Brown', team: 'Product', location: 'Singapore', jobRole: 'Product Manager', imageUrl: '' },
