import React, { useEffect, useState } from 'react';
import Tree, { RawNodeDatum, CustomNodeElementProps } from 'react-d3-tree';
import { User } from '../types/User';
import { useUsers } from './UserContext';
import {
  Box,
  Card,
  Avatar,
  Typography,
  useTheme as useMuiTheme,
  alpha
} from '@mui/material';
import { useTheme } from './ThemeContext';

type NodeAttributes = {
  name: string;
  role: string;
  team: string;
  location: string;
  email: string;
}

// Convert NodeAttributes to Record<string, string | number | boolean>
type TreeNodeAttributes = Record<keyof NodeAttributes, string>;

interface OrgChartNodeData {
  name: string;
  attributes?: TreeNodeAttributes;
  children?: OrgChartNodeData[];
}

const stringToColor = (string: string) => {
  let hash = 0;
  for (let i = 0; i < string.length; i++) {
    hash = string.charCodeAt(i) + ((hash << 5) - hash);
  }
  let color = '#';
  for (let i = 0; i < 3; i++) {
    const value = (hash >> (i * 8)) & 0xFF;
    color += ('00' + value.toString(16)).substr(-2);
  }
  return color;
};

const convertToRawNodeDatum = (node: OrgChartNodeData): RawNodeDatum => {
  return {
    name: node.name,
    attributes: node.attributes,
    children: node.children?.map(convertToRawNodeDatum)
  };
};

const OrgChart: React.FC = () => {
  const muiTheme = useMuiTheme();
  const { headingFontWeights, pageTitleFontSize, headingFont } = useTheme();
  const { users } = useUsers();
  const [treeData, setTreeData] = useState<OrgChartNodeData | null>(null);

  useEffect(() => {
    const buildTreeData = (users: User[]): OrgChartNodeData | null => {
      if (!users.length) return null;

      // Create a map for quick user lookup
      const userMap = new Map(users.map(user => [user.id, user]));

      // Find the root (CEO or highest level manager)
      const root = users.find(user => {
        // Check if this user's manager exists in our user list
        const hasValidManager = user.managerId && userMap.has(user.managerId);
        return !hasValidManager && (
          user.jobRole.toLowerCase().includes('ceo') || 
          user.jobRole.toLowerCase().includes('chief') ||
          user.directReports?.length
        );
      }) || users[0]; // Fallback to first user if no clear root

      const buildNode = (user: User): OrgChartNodeData => {
        // Find direct reports both from directReports array and managerId references
        const directReportIds = new Set<number>();

        // Add explicitly defined direct reports
        if (user.directReports) {
          user.directReports.forEach(id => directReportIds.add(id));
        }

        // Add users who reference this user as their manager
        users.forEach(u => {
          if (u.managerId === user.id) {
            directReportIds.add(u.id);
          }
        });

        // Build children nodes
        const children = Array.from(directReportIds)
          .map(id => userMap.get(id))
          .filter((u): u is User => u !== undefined)
          .map(child => buildNode(child));

        return {
          name: user.name,
          attributes: {
            name: user.name,
            role: user.jobRole,
            team: user.team,
            location: user.location,
            email: user.email
          },
          children: children.length > 0 ? children : undefined
        };
      };

      return buildNode(root);
    };

    const data = buildTreeData(users);
    setTreeData(data);
  }, [users]);

  const renderCustomNode = ({ nodeDatum }: CustomNodeElementProps) => {
    if (!nodeDatum.attributes) return null;

    const attrs = nodeDatum.attributes as Record<string, string>;
    const initials = attrs.name.split(' ').map(n => n[0]).join('');
    const bgColor = stringToColor(attrs.name);

    return (
      <g>
        <foreignObject x={-100} y={-50} width={200} height={100}>
          <Card
            sx={{
              p: 1.5,
              width: '100%',
              height: '100%',
              bgcolor: alpha(muiTheme.palette.background.paper, 0.9),
              boxShadow: 3,
              borderRadius: 2,
              overflow: 'visible'
            }}
          >
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
              <Avatar
                sx={{
                  width: 45,
                  height: 45,
                  bgcolor: bgColor,
                  fontSize: '1.1rem',
                  fontWeight: 600
                }}
              >
                {initials}
              </Avatar>
              <Box sx={{ minWidth: 0, flex: 1 }}>
                <Typography 
                  variant="subtitle1" 
                  sx={{ 
                    fontWeight: 600,
                    whiteSpace: 'nowrap',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis'
                  }}
                >
                  {attrs.name}
                </Typography>
                <Typography 
                  variant="caption" 
                  color="textSecondary"
                  sx={{
                    display: 'block',
                    whiteSpace: 'nowrap',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis'
                  }}
                >
                  {attrs.role}
                </Typography>
                <Typography 
                  variant="caption" 
                  color="primary"
                  sx={{
                    display: 'block',
                    whiteSpace: 'nowrap',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis'
                  }}
                >
                  {attrs.team}
                </Typography>
              </Box>
            </Box>
          </Card>
        </foreignObject>
      </g>
    );
  };

  if (!treeData) {
    return (
      <Box sx={{ p: 3 }}>
        <Typography>Loading organization chart...</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ height: '100vh', p: 3 }}>
      <Typography 
        component="h1" 
        sx={{ 
          mb: 3,
          fontWeight: headingFontWeights?.pageTitle || 700,
          fontSize: pageTitleFontSize,
          fontFamily: headingFont
        }}
      >
        Organization Chart
      </Typography>
      <Box sx={{ width: '100%', height: 'calc(100vh - 64px)' }}>
        <Tree
          data={treeData ? convertToRawNodeDatum(treeData) : { name: 'No Data' }}
          renderCustomNodeElement={renderCustomNode}
          orientation="vertical"
          pathFunc="step"
          translate={{ x: window.innerWidth / 2, y: 100 }}
          separation={{ siblings: 2.5, nonSiblings: 3 }}
          zoom={0.6}
          nodeSize={{ x: 300, y: 150 }}
          enableLegacyTransitions={true}
          transitionDuration={800}
        />
      </Box>
    </Box>
  );
};

export default OrgChart;
