import React, { useState, useEffect } from 'react';
import { useUsers } from './UserContext';
import {
    Grid,
    Card,
    CardContent,
    Typography,
    TextField,
    Box,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    Pagination,
    IconButton,
    Button,
    useTheme,
    useMediaQuery,
    Stack,
    Collapse,
    Tooltip,
    Accordion,
    AccordionSummary,
    AccordionDetails,
    Avatar,
    Link,
    Drawer
} from '@mui/material';
import FilterListIcon from '@mui/icons-material/FilterList';
import CloseIcon from '@mui/icons-material/Close';
import EmailIcon from '@mui/icons-material/Email';
import PhoneIcon from '@mui/icons-material/Phone';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import FileDownloadIcon from '@mui/icons-material/FileDownload';
import { User } from '../types/User';

const ITEMS_PER_PAGE = 20;

const UserList: React.FC = () => {
    const { users } = useUsers();
    const [showFilters, setShowFilters] = useState(true);
    const [drawerOpen, setDrawerOpen] = useState(false);
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down('md'));
    const [filteredUsers, setFilteredUsers] = useState<User[]>([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [teamFilter, setTeamFilter] = useState('');
    const [locationFilter, setLocationFilter] = useState('');
    const [roleFilter, setRoleFilter] = useState('');
    const [page, setPage] = useState(1);

    const teams = Array.from(new Set(users.map(user => user.team)));
    const locations = Array.from(new Set(users.map(user => user.location)));
    const roles = Array.from(new Set(users.map(user => user.jobRole)));

    useEffect(() => {
        let filtered = users;

        if (searchTerm) {
            filtered = filtered.filter(user =>
                user.name.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        if (teamFilter) {
            filtered = filtered.filter(user => user.team === teamFilter);
        }

        if (locationFilter) {
            filtered = filtered.filter(user => user.location === locationFilter);
        }

        if (roleFilter) {
            filtered = filtered.filter(user => user.jobRole === roleFilter);
        }

        setFilteredUsers(filtered);
        setPage(1);
    }, [users, searchTerm, teamFilter, locationFilter, roleFilter]);

    const paginatedUsers = filteredUsers.slice(
        (page - 1) * ITEMS_PER_PAGE,
        page * ITEMS_PER_PAGE
    );

    const handlePageChange = (event: React.ChangeEvent<unknown>, value: number) => {
        setPage(value);
    };

    const exportToCSV = () => {
        const headers = [
            'Name',
            'Team',
            'Location',
            'Job Role',
            'Email',
            'Phone',
            'LinkedIn'
        ];

        const csvData = filteredUsers.map(user => [
            user.name,
            user.team,
            user.location,
            user.jobRole,
            user.email,
            user.phone,
            user.linkedin
        ]);

        const csvContent = [
            headers.join(','),
            ...csvData.map(row => row.join(','))
        ].join('\n');

        const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
        const link = document.createElement('a');
        const url = URL.createObjectURL(blob);
        link.setAttribute('href', url);
        link.setAttribute('download', 'users.csv');
        link.style.visibility = 'hidden';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    const FilterControls = () => (
        <Stack spacing={2} sx={{ width: '100%' }}>
            <TextField
                fullWidth
                label="Search by name"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <FormControl fullWidth>
                <InputLabel>Team</InputLabel>
                <Select
                    value={teamFilter}
                    label="Team"
                    onChange={(e) => setTeamFilter(e.target.value)}
                >
                    <MenuItem value="">All</MenuItem>
                    {teams.map(team => (
                        <MenuItem key={team} value={team}>{team}</MenuItem>
                    ))}
                </Select>
            </FormControl>
            <FormControl fullWidth>
                <InputLabel>Location</InputLabel>
                <Select
                    value={locationFilter}
                    label="Location"
                    onChange={(e) => setLocationFilter(e.target.value)}
                >
                    <MenuItem value="">All</MenuItem>
                    {locations.map(location => (
                        <MenuItem key={location} value={location}>{location}</MenuItem>
                    ))}
                </Select>
            </FormControl>
            <FormControl fullWidth>
                <InputLabel>Role</InputLabel>
                <Select
                    value={roleFilter}
                    label="Role"
                    onChange={(e) => setRoleFilter(e.target.value)}
                >
                    <MenuItem value="">All</MenuItem>
                    {roles.map(role => (
                        <MenuItem key={role} value={role}>{role}</MenuItem>
                    ))}
                </Select>
            </FormControl>
            <Button
                variant="outlined"
                onClick={() => {
                    setSearchTerm('');
                    setTeamFilter('');
                    setLocationFilter('');
                    setRoleFilter('');
                }}
                disabled={!searchTerm && !teamFilter && !locationFilter && !roleFilter}
                sx={{
                    borderColor: 'divider',
                    color: 'text.secondary',
                    '&:hover': {
                        backgroundColor: 'action.hover',
                    }
                }}
            >
                Clear All
            </Button>
        </Stack>
    );

    return (
        <Box sx={{ width: '100%', pt: 2 }}>
            <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Typography variant="h4" component="h1">
                    Users
                </Typography>
                <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
                    {!isMobile && (
                        <Tooltip title={showFilters ? "Hide filters" : "Show filters"}>
                            <IconButton 
                                onClick={() => setShowFilters(!showFilters)}
                                size="small"
                                sx={{ 
                                    bgcolor: showFilters ? 'action.selected' : 'transparent',
                                    '&:hover': {
                                        bgcolor: showFilters ? 'action.selected' : 'action.hover'
                                    },
                                    transition: 'transform 0.2s ease-in-out',
                                    transform: showFilters ? 'rotate(-180deg)' : 'rotate(0)',
                                }}
                            >
                                <FilterListIcon />
                            </IconButton>
                        </Tooltip>
                    )}
                    {isMobile && (
                        <Button
                            variant="outlined"
                            startIcon={<FilterListIcon />}
                            onClick={() => setDrawerOpen(true)}
                        >
                            Filters
                        </Button>
                    )}
                    <Button
                        variant="outlined"
                        startIcon={<FileDownloadIcon />}
                        onClick={exportToCSV}
                        sx={{
                            borderColor: 'divider',
                            color: 'text.primary',
                            '&:hover': {
                                borderColor: 'primary.main',
                                backgroundColor: theme.palette.action.hover
                            }
                        }}
                    >
                        Export to CSV
                    </Button>
                </Box>
            </Box>
            <Box sx={{ 
                display: 'grid', 
                gridTemplateColumns: isMobile ? '1fr' : '250px 1fr',
                gap: 3,
                mb: 4 
            }}>
                {!isMobile && (
                    <Collapse in={showFilters} orientation="horizontal">
                        <Box component="aside" sx={{ position: 'sticky', top: 24, alignSelf: 'start', width: 250 }}>
                            <FilterControls />
                        </Box>
                    </Collapse>
                )}
                <Box>
                    <Grid container spacing={3}>
                        {paginatedUsers.map((user) => (
                            <Grid item xs={12} sm={6} md={4} key={user.id}>
                                <Card>
                                    <CardContent>
                                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                                            <Avatar
                                                src={user.imageUrl || 'https://via.placeholder.com/200'}
                                                alt={user.name}
                                                sx={{ width: 56, height: 56, mr: 2 }}
                                            />
                                            <Box>
                                                <Typography variant="h6" component="h2">
                                                    {user.name}
                                                </Typography>
                                                <Typography variant="body2" color="text.secondary" noWrap>
                                                    {user.jobRole}
                                                </Typography>
                                                <Typography variant="body2" color="text.secondary" noWrap>
                                                    {user.team} Â· {user.location}
                                                </Typography>
                                            </Box>
                                        </Box>
                                        <Accordion elevation={0} sx={{ 
                                            '&:before': {
                                                display: 'none',
                                            },
                                            boxShadow: 'none',
                                        }}>
                                            <AccordionSummary
                                                expandIcon={<ExpandMoreIcon />}
                                                aria-controls="contact-content"
                                                id="contact-header"
                                                sx={{
                                                    borderTop: '1px solid rgba(0, 0, 0, 0.12)',
                                                    minHeight: '48px',
                                                    '& .MuiAccordionSummary-content': {
                                                        margin: '12px 0',
                                                    }
                                                }}
                                            >
                                                <Typography>Contact Information</Typography>
                                            </AccordionSummary>
                                            <AccordionDetails>
                                                <Link 
                                                    href={`mailto:${user.email}`}
                                                    color="inherit"
                                                    sx={{ 
                                                        display: 'flex',
                                                        alignItems: 'center',
                                                        mb: 1,
                                                        textDecoration: 'none',
                                                        '&:hover': { textDecoration: 'underline' }
                                                    }}
                                                >
                                                    <EmailIcon fontSize="small" sx={{ mr: 1 }} />
                                                    <Typography variant="body2">{user.email}</Typography>
                                                </Link>
                                                <Link
                                                    href={`tel:${user.phone}`}
                                                    color="inherit"
                                                    sx={{
                                                        display: 'flex',
                                                        alignItems: 'center',
                                                        mb: 1,
                                                        textDecoration: 'none',
                                                        '&:hover': { textDecoration: 'underline' }
                                                    }}
                                                >
                                                    <PhoneIcon fontSize="small" sx={{ mr: 1 }} />
                                                    <Typography variant="body2">{user.phone}</Typography>
                                                </Link>
                                                <Link
                                                    href={`https://www.${user.linkedin}`}
                                                    target="_blank"
                                                    rel="noopener noreferrer"
                                                    color="inherit"
                                                    sx={{
                                                        display: 'flex',
                                                        alignItems: 'center',
                                                        textDecoration: 'none',
                                                        '&:hover': { textDecoration: 'underline' }
                                                    }}
                                                >
                                                    <LinkedInIcon fontSize="small" sx={{ mr: 1 }} />
                                                    <Typography variant="body2">{user.linkedin}</Typography>
                                                </Link>
                                            </AccordionDetails>
                                        </Accordion>
                                    </CardContent>
                                </Card>
                            </Grid>
                        ))}
                    </Grid>

                    {isMobile && (
                        <Drawer
                            anchor="right"
                            open={drawerOpen}
                            onClose={() => setDrawerOpen(false)}
                            PaperProps={{
                                sx: {
                                    width: '100%',
                                    maxWidth: '360px',
                                    p: 2,
                                }
                            }}
                        >
                            <Box sx={{ 
                                display: 'flex', 
                                alignItems: 'center', 
                                justifyContent: 'space-between',
                                mb: 2
                            }}>
                                <Typography variant="h6">Filters</Typography>
                                <IconButton onClick={() => setDrawerOpen(false)}>
                                    <CloseIcon />
                                </IconButton>
                            </Box>
                            <FilterControls />
                        </Drawer>
                    )}
                </Box>
            </Box>

            <Box sx={{ 
                mt: 2, 
                mb: 3, 
                display: 'flex', 
                alignItems: 'center',
                color: 'text.secondary',
                typography: 'body2'
            }}>
                <Typography component="span">
                    Showing {filteredUsers.length} {filteredUsers.length === 1 ? 'result' : 'results'}
                    {filteredUsers.length !== users.length && ` out of ${users.length} total users`}
                </Typography>
                {(searchTerm || teamFilter || locationFilter || roleFilter) && (
                    <Typography 
                        component="span" 
                        sx={{ 
                            ml: 1,
                            color: 'text.disabled',
                            fontSize: '0.875rem'
                        }}
                    >
                        with applied filters
                    </Typography>
                )}
            </Box>

            {filteredUsers.length > ITEMS_PER_PAGE && (
                <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center' }}>
                    <Pagination
                        count={Math.ceil(filteredUsers.length / ITEMS_PER_PAGE)}
                        page={page}
                        onChange={handlePageChange}
                        color="primary"
                    />
                </Box>
            )}
        </Box>
    );
};

export default UserList;
