import React, { useEffect, useState } from 'react';
import Tree, { RawNodeDatum, CustomNodeElementProps } from 'react-d3-tree';
import { User } from '../types/User';
import { useUsers } from './UserContext';
import {
  Box,
  Card,
  Avatar,
  Typography,
  useTheme as useMuiTheme,
  alpha
} from '@mui/material';
import { useTheme } from './ThemeContext';

type NodeAttributes = {
  name: string;
  role: string;
  team: string;
  location: string;
  email: string;
}

// Update the TreeNodeAttributes interface to include the additional fields
type TreeNodeAttributes = Record<keyof NodeAttributes, string> & {
  id: string;
  hasChildren: boolean;
};

interface OrgChartNodeData {
  name: string;
  attributes?: TreeNodeAttributes;
  children?: OrgChartNodeData[];
  id: string;
}

const stringToColor = (string: string) => {
  let hash = 0;
  for (let i = 0; i < string.length; i++) {
    hash = string.charCodeAt(i) + ((hash << 5) - hash);
  }
  let color = '#';
  for (let i = 0; i < 3; i++) {
    const value = (hash >> (i * 8)) & 0xFF;
    color += ('00' + value.toString(16)).substr(-2);
  }
  return color;
};

const convertToRawNodeDatum = (node: OrgChartNodeData): RawNodeDatum => {
  return {
    name: node.name,
    attributes: node.attributes,
    children: node.children?.map(convertToRawNodeDatum)
  };
};

const OrgChart: React.FC = () => {
  const muiTheme = useMuiTheme();
  const { headingFontWeights, pageTitleFontSize, headingFont } = useTheme();
  const { users } = useUsers();
  const [treeData, setTreeData] = useState<OrgChartNodeData | null>(null);
  const [dimensions, setDimensions] = useState({ 
    width: window.innerWidth, 
    height: window.innerHeight 
  });
  const [expandedNodes, setExpandedNodes] = useState<Set<string>>(new Set());

  // Update dimensions on window resize
  useEffect(() => {
    const handleResize = () => {
      setDimensions({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    const buildTreeData = (users: User[]): OrgChartNodeData | null => {
      if (!users.length) return null;

      const userMap = new Map(users.map(user => [user.id, user]));

      // Find John Doe or fallback to the root
      const root = users.find(user => 
        user.name === "John Doe" || (
          !user.managerId && (
            user.jobRole.toLowerCase().includes('ceo') || 
            user.jobRole.toLowerCase().includes('chief') ||
            user.directReports?.length
          )
        )
      ) || users[0];

      const buildNode = (user: User): OrgChartNodeData => {
        const directReportIds = new Set<number>();

        if (user.directReports) {
          user.directReports.forEach(id => directReportIds.add(id));
        }

        users.forEach(u => {
          if (u.managerId === user.id) {
            directReportIds.add(u.id);
          }
        });

        const children = Array.from(directReportIds)
          .map(id => userMap.get(id))
          .filter((u): u is User => u !== undefined)
          .map(child => buildNode(child));

        const hasChildren = children.length > 0;

        return {
          name: user.name,
          id: user.id.toString(),
          attributes: {
            name: user.name,
            role: user.jobRole,
            team: user.team,
            location: user.location,
            email: user.email,
            id: user.id.toString(),
            hasChildren
          },
          children: hasChildren ? children : undefined
        };
      };

      const data = buildNode(root);

      // Initialize expanded nodes with only the root node
      const initialExpandedNodes = new Set<string>([data.id]);

      // Add only the first level children to expanded nodes
      if (data.children) {
        data.children.forEach(child => {
          initialExpandedNodes.add(child.id);
        });
      }

      setExpandedNodes(initialExpandedNodes);
      return data;
    };

    const data = buildTreeData(users);
    setTreeData(data);
  }, [users]);

  const toggleNode = (nodeId: string) => {
    setExpandedNodes(prev => {
      const next = new Set(prev);
      if (next.has(nodeId)) {
        next.delete(nodeId);
      } else {
        next.add(nodeId);
      }
      return next;
    });
  };

  const processTreeData = (node: OrgChartNodeData): RawNodeDatum => {
    return {
      name: node.name,
      attributes: {
        ...node.attributes,
        id: node.id,
        hasChildren: Boolean(node.children && node.children.length > 0)
      },
      children: expandedNodes.has(node.id) 
        ? node.children?.map(processTreeData)
        : undefined
    };
  };

  const renderCustomNode = ({ nodeDatum }: CustomNodeElementProps): React.ReactElement => {
    const attrs = nodeDatum.attributes as TreeNodeAttributes;
    const isMobile = dimensions.width < 600;

    // Return a default node if attributes are missing
    if (!attrs) {
      return (
        <g>
          <foreignObject 
            x={isMobile ? -40 : -60} 
            y={isMobile ? -20 : -25} 
            width={isMobile ? 80 : 120} 
            height={isMobile ? 40 : 50}
          >
            <Card
              sx={{
                p: isMobile ? 0.5 : 1,
                width: '100%',
                height: '100%',
                bgcolor: alpha(muiTheme.palette.background.paper, 0.9),
                boxShadow: 1,
                borderRadius: 1
              }}
            >
              <Typography 
                variant={isMobile ? "caption" : "body2"} 
                align="center" 
                display="block"
              >
                {nodeDatum.name}
              </Typography>
            </Card>
          </foreignObject>
        </g>
      );
    }

    const initials = attrs.name.split(' ').map(n => n[0]).join('');
    const bgColor = stringToColor(attrs.name);
    const hasChildren = attrs.hasChildren;
    const isExpanded = expandedNodes.has(attrs.id);

    return (
      <g>
        <foreignObject 
          x={isMobile ? -75 : -100} 
          y={isMobile ? -40 : -50} 
          width={isMobile ? 150 : 200} 
          height={isMobile ? 80 : 100}
        >
          <Card
            sx={{
              p: isMobile ? 1 : 1.5,
              width: '100%',
              height: '100%',
              bgcolor: alpha(muiTheme.palette.background.paper, 0.9),
              boxShadow: 3,
              borderRadius: 2,
              overflow: 'visible',
              position: 'relative',
              cursor: hasChildren ? 'pointer' : 'default',
              '&:hover': hasChildren ? {
                bgcolor: alpha(muiTheme.palette.background.paper, 0.95),
              } : {}
            }}
            onClick={() => hasChildren && toggleNode(attrs.id)}
          >
            {hasChildren && (
              <Box
                sx={{
                  position: 'absolute',
                  right: 4,
                  top: 4,
                  width: 20,
                  height: 20,
                  borderRadius: '50%',
                  bgcolor: alpha(muiTheme.palette.primary.main, 0.1),
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '1rem',
                  color: 'primary.main'
                }}
              >
                {isExpanded ? '−' : '+'}
              </Box>
            )}
            <Box sx={{ display: 'flex', alignItems: 'center', gap: isMobile ? 1 : 1.5 }}>
              <Avatar
                sx={{
                  width: isMobile ? 35 : 45,
                  height: isMobile ? 35 : 45,
                  bgcolor: bgColor,
                  fontSize: isMobile ? '0.9rem' : '1.1rem',
                  fontWeight: 600
                }}
              >
                {initials}
              </Avatar>
              <Box sx={{ minWidth: 0, flex: 1 }}>
                <Typography 
                  variant={isMobile ? "body2" : "subtitle1"}
                  sx={{ 
                    fontWeight: 600,
                    whiteSpace: 'nowrap',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    fontSize: isMobile ? '0.8rem' : 'inherit',
                    pr: hasChildren ? 3 : 0
                  }}
                >
                  {attrs.name}
                </Typography>
                <Typography 
                  variant="caption" 
                  color="textSecondary"
                  sx={{
                    display: 'block',
                    whiteSpace: 'nowrap',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    fontSize: isMobile ? '0.7rem' : 'inherit'
                  }}
                >
                  {attrs.role}
                </Typography>
                <Typography 
                  variant="caption" 
                  color="primary"
                  sx={{
                    display: 'block',
                    whiteSpace: 'nowrap',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    fontSize: isMobile ? '0.7rem' : 'inherit'
                  }}
                >
                  {attrs.team}
                </Typography>
              </Box>
            </Box>
          </Card>
        </foreignObject>
      </g>
    );
  };

  if (!treeData) {
    return (
      <Box sx={{ p: 3 }}>
        <Typography>Loading organization chart...</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ height: '100vh', p: 3 }}>
      <Typography 
        component="h1" 
        sx={{ 
          mb: 3,
          fontWeight: headingFontWeights?.pageTitle || 700,
          fontSize: pageTitleFontSize,
          fontFamily: headingFont
        }}
      >
        Organization Chart
      </Typography>
      <Box sx={{ 
        width: '100%', 
        height: 'calc(100vh - 64px)',
        overflow: 'hidden',
        touchAction: 'none' // Prevents default touch behaviors
      }}>
        <Tree
          data={treeData ? processTreeData(treeData) : { name: 'No Data' }}
          renderCustomNodeElement={renderCustomNode}
          orientation="vertical"
          pathFunc="step"
          translate={{ 
            x: dimensions.width / 2, 
            y: Math.min(100, dimensions.height * 0.1) 
          }}
          separation={{ 
            siblings: 2, 
            nonSiblings: 2 
          }}
          zoom={0.6}
          nodeSize={{ 
            x: 300, 
            y: 150 
          }}
          enableLegacyTransitions={true}
          transitionDuration={800}
          scaleExtent={{ min: 0.1, max: 2 }}
          hasInteractiveNodes={true}
        />
      </Box>
    </Box>
  );
};

export default OrgChart;
