import React from 'react';
import {
    Box,
    Typography,
    Card,
    CardContent,
    useTheme,
    Avatar,
    Link,
    Stack,
    Tooltip,
    Paper
} from '@mui/material';
import {
    Email as EmailIcon,
    Phone as PhoneIcon,
    LinkedIn as LinkedInIcon,
    LocationOn as LocationIcon,
    Business as BusinessIcon,
} from '@mui/icons-material';
import { ReactTree } from '@naisutech/react-tree';
import { User } from '../types/User';

interface TreeNode {
    id: string;
    label: string;
    children?: TreeNode[];
    data?: User;
}

const UserProfileCard: React.FC<{ user: User }> = ({ user }) => {
    const theme = useTheme();

    return (
        <Card sx={{ 
            width: 280,
            m: 1,
            transition: 'all 0.2s ease-in-out',
            '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: theme.shadows[8]
            }
        }}>
            <CardContent>
                <Stack spacing={2}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <Avatar
                            src={user.imageUrl || `https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=random`}
                            alt={user.name}
                            sx={{ width: 56, height: 56 }}
                        />
                        <Box>
                            <Typography variant="h6" sx={{ fontSize: '1rem' }}>
                                {user.name}
                            </Typography>
                            <Typography color="text.secondary" variant="body2">
                                {user.jobRole}
                            </Typography>
                        </Box>
                    </Box>

                    <Stack spacing={1}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <BusinessIcon fontSize="small" color="action" />
                            <Typography variant="body2" color="text.secondary">
                                {user.team}
                            </Typography>
                        </Box>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <LocationIcon fontSize="small" color="action" />
                            <Typography variant="body2" color="text.secondary">
                                {user.location}
                            </Typography>
                        </Box>
                        <Tooltip title="Send email">
                            <Link 
                                href={`mailto:${user.email}`}
                                sx={{ 
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: 1,
                                    color: 'inherit',
                                    textDecoration: 'none',
                                    '&:hover': { color: theme.palette.primary.main }
                                }}
                            >
                                <EmailIcon fontSize="small" color="action" />
                                <Typography variant="body2" noWrap>
                                    {user.email}
                                </Typography>
                            </Link>
                        </Tooltip>
                        <Tooltip title="Call">
                            <Link 
                                href={`tel:${user.phone}`}
                                sx={{ 
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: 1,
                                    color: 'inherit',
                                    textDecoration: 'none',
                                    '&:hover': { color: theme.palette.primary.main }
                                }}
                            >
                                <PhoneIcon fontSize="small" color="action" />
                                <Typography variant="body2">
                                    {user.phone}
                                </Typography>
                            </Link>
                        </Tooltip>
                        <Tooltip title="View LinkedIn profile">
                            <Link 
                                href={`https://www.${user.linkedin}`}
                                target="_blank"
                                rel="noopener noreferrer"
                                sx={{ 
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: 1,
                                    color: 'inherit',
                                    textDecoration: 'none',
                                    '&:hover': { color: theme.palette.primary.main }
                                }}
                            >
                                <LinkedInIcon fontSize="small" color="action" />
                                <Typography variant="body2" noWrap>
                                    {user.linkedin}
                                </Typography>
                            </Link>
                        </Tooltip>
                    </Stack>
                </Stack>
            </CardContent>
        </Card>
    );
};

const buildTreeData = (users: User[]): TreeNode[] => {
    const buildNode = (user: User): TreeNode => {
        const directReports = users.filter(u => u.managerId === user.id);
        return {
            id: user.id.toString(),
            label: user.name,
            data: user,
            children: directReports.map(report => buildNode(report))
        };
    };

    const rootUser = users.find(user => !user.managerId);
    return rootUser ? [buildNode(rootUser)] : [];
};

const CustomNode: React.FC<{ node: TreeNode }> = ({ node }) => {
    if (!node.data) return null;
    return <UserProfileCard user={node.data} />;
};

const OrgChart: React.FC = () => {
    const theme = useTheme();
    const treeData = buildTreeData(mockUsers);

    if (!treeData.length) {
        return (
            <Typography color="error">
                No root user found in the organization
            </Typography>
        );
    }

    return (
        <Box sx={{ width: '100%', pt: 2 }}>
            <Typography variant="h4" component="h1" sx={{ mb: 4 }}>
                Organization Chart
            </Typography>
            <Paper sx={{ 
                minHeight: 'calc(100vh - 180px)',
                p: 4,
                overflowX: 'auto'
            }}>
                <ReactTree
                    data={treeData}
                    nodeRenderer={CustomNode}
                    defaultOpen={true}
                    style={{
                        backgroundColor: 'transparent',
                        color: theme.palette.text.primary,
                        '--tree-line-color': theme.palette.divider,
                        '--tree-line-width': '2px',
                        '--tree-line-border-radius': '4px',
                        '--tree-node-padding': '8px',
                        '--tree-node-margin': '8px'
                    }}
                />
            </Paper>
        </Box>
    );
};

const mockUsers: User[] = [
    {
        id: 1,
        name: 'John Doe',
        team: 'Executive',
        location: 'New York',
        jobRole: 'CEO',
        imageUrl: '',
        email: 'john.doe@company.com',
        phone: '+1 (555) 000-0001',
        linkedin: 'linkedin.com/in/johndoe',
        directReports: [2, 3]
    },
    {
        id: 2,
        name: 'Sarah Williams',
        team: 'Engineering',
        location: 'San Francisco',
        jobRole: 'CTO',
        imageUrl: '',
        email: 'sarah.williams@company.com',
        phone: '+1 (555) 000-0002',
        linkedin: 'linkedin.com/in/sarahwilliams',
        managerId: 1,
        directReports: [4, 5]
    },
    {
        id: 3,
        name: 'Michael Brown',
        team: 'Operations',
        location: 'Chicago',
        jobRole: 'COO',
        imageUrl: '',
        email: 'michael.brown@company.com',
        phone: '+1 (555) 000-0003',
        linkedin: 'linkedin.com/in/michaelbrown',
        managerId: 1,
        directReports: [6, 7]
    },
    {
        id: 4,
        name: 'Emily Davis',
        team: 'Engineering',
        location: 'Seattle',
        jobRole: 'Engineering Manager',
        imageUrl: '',
        email: 'emily.davis@company.com',
        phone: '+1 (555) 000-0004',
        linkedin: 'linkedin.com/in/emilydavis',
        managerId: 2
    },
    {
        id: 5,
        name: 'David Wilson',
        team: 'Engineering',
        location: 'Boston',
        jobRole: 'Tech Lead',
        imageUrl: '',
        email: 'david.wilson@company.com',
        phone: '+1 (555) 000-0005',
        linkedin: 'linkedin.com/in/davidwilson',
        managerId: 2
    },
    {
        id: 6,
        name: 'Lisa Anderson',
        team: 'Operations',
        location: 'Miami',
        jobRole: 'Operations Manager',
        imageUrl: '',
        email: 'lisa.anderson@company.com',
        phone: '+1 (555) 000-0006',
        linkedin: 'linkedin.com/in/lisaanderson',
        managerId: 3
    },
    {
        id: 7,
        name: 'Robert Taylor',
        team: 'Operations',
        location: 'Houston',
        jobRole: 'Project Manager',
        imageUrl: '',
        email: 'robert.taylor@company.com',
        phone: '+1 (555) 000-0007',
        linkedin: 'linkedin.com/in/roberttaylor',
        managerId: 3
    }
];

export default OrgChart;
